import React, { useEffect } from "react";
import { useRouter } from "next/router";
import { format } from "date-fns";

import { ErrorMessage, Formik, Form, Field } from "formik";
import { Grid, Typography, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient } from "@apollo/react-hooks";
import { getCandidateRegistrationInfoQuery } from "../queries&Mutations&Functions/Queries";

import { removeTypename } from "../queries&Mutations&Functions/Functions";
import useForm from "../utils/useForm";

const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",

    // fontSize: 100,
  },

  picStyled: {
    // paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
    borderRadius: "0.5rem",
  },

  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    padding: "1rem",
    maxWidth: "90%",
    // backgroundColor: "#c89666",
  },
  generalContainer: {
    display: "flex",
    flexDirection:"row",
  },
  examInfo: {
    display: "grid",
    gridTemplateColumns: "2fr 1fr",
    padding: "1rem",
    backgroundColor: "#c89666",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    // minWidth: "30vw",
    // minWidth: "40%",
    // maxWidth: "100vw",
