const NewRegistrationReceipt = ({ id }) => {
  const classes = useStyles();
  const router = useRouter();
  const client = useApolloClient();

  const [state, setState] = useForm({});

  const loadRegistrationData = async () => {
    const { error, data } = await client.query({
      query: getCandidateRegistrationInfoQuery,
      variables: { id },
    });

    console.log(data);
    const { registration } = data;
    setState({ registration });
    return data;
  };

  useEffect(() => {
    loadRegistrationData();
  }, [id]);

  console.log(state.registration);
  const {
    aptitude,

    candidate,
    centerExamSessionSpecialty,
    createdAt,
    candRegistrationNumber,
  } = { ...state.registration };

  const { centerExamSession, specialty } = { ...centerExamSessionSpecialty };

  const { examSession, center } = { ...centerExamSession };
  const { exam, session } = { ...examSession };
  const { centerName } = { ...center };
  const { examName } = { ...exam };
  const { sessionName } = { ...session };
  const { specialtyName, subjectSpecialty } = { ...specialty };

  const {
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    gender,
    placeOfBirth,
    dateOfBirth,
    EPF1,
    EPF2,
  } = { ...candidate };

  const initialValues = {
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    EPF1: "",
    EPF2: "",
    placeOfBirth: "",
    dateOfBirth: "",
  };

  return (
    <Formik initialValues={initialValues}>
      {({ submitForm, setFieldValue, isSubmitting }) => {
        return (
          <div className={classes.centerAll}>
            <Paper className={classes.pageStyled} elevation={13}>
              <Form aria-busy={isSubmitting}>
                {isSubmitting && <LinearProgress />}
                <Grid className={classes.centerAll} container>
                  <Grid container className={classes.centerAll}>
                    <Grid item>
                      <Typography
                        className={classes.titleStyled}
                        gutterBottom
                        variant="h5"
                        component="h6"
                      >
                        Recu d'Inscription aux examens
                      </Typography>
                    </Grid>
                  </Grid>
                  <Grid spacing={10} container className={classes.examInfo}>
                    <Grid xs={12} sm={6} container>
                      <Grid item xs={12}>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Centre D'Examen: </strong>
                            {centerName}
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Examen: </strong> {examName}
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Session: </strong> {sessionName}
                          </p>
                        </Typography>
                      </Grid>
                      <Grid item xs={12}>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong>Info Inscritption: </strong>
                            <hr />
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Numero: </strong> {candRegistrationNumber}
                          </p>
                        </Typography>

                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Aptitude: </strong> {aptitude}
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="h"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> SERIE: </strong>
                            {specialtyName}
                          </p>
                        </Typography>
                        <Typography variant="body1">
                          <p>
                            <strong>Date : </strong>
                            {format(createdAt, " DD, MMM, YYYY ")}
                          </p>
                        </Typography>
                      </Grid>
                    </Grid>
                    <Grid xs={12} sm={6} item>
                      {
                        <img
                          className={classes.picStyled}
                          src={image}
                          alt={cand1stName}
                        />
                      }
                    </Grid>
                  </Grid>
                  <Grid
                    spacing={10}
                    container
                    className={classes.generalContainer}
                  >
                    <Grid xs={12} sm={6} item>
                      <Typography variant="body1">
                        <p>
                          <strong>Nom:</strong>
                          {cand1stName}
                        </p>
                      </Typography>
                      <Typography variant="body1">
                        <p>
                          <strong>Prenoms: </strong>
                          {cand2ndName}
                        </p>
                      </Typography>
                      <Typography variant="body1">
                        <p>
                          <strong>Autres Noms:</strong>
                          {cand3rdName}
                        </p>
                      </Typography>
                      
                      <Typography variant="body1">
                        <p>
                          <strong>Sexe:</strong> {gender}
                        </p>
                      </Typography>
                      <Typography variant="body1">
                        <p>
                          <strong>Date : </strong>
                          {format(dateOfBirth, " DD, MMM, YYYY ")}
                        </p>
                      </Typography>
                      </Grid>

                      <Grid xs={12} sm={6} item>
                      <Typography variant="body1">
                        <p>
                          <strong>Lieu:</strong> {placeOfBirth}
                        </p>
                      </Typography>
                      <Typography variant="body1">
                        <p>
                          <strong>Tel: </strong> {email}
                        </p>
                      </Typography>
                      <Typography variant="body1">
                        <p>
                          <strong>Email:</strong>
                          {email}
                        </p>
                      </Typography>

                      <Typography variant="body1">
                        <p>
                          <strong>EPF1:</strong> <p> {EPF1}</p>
                        </p>
                      </Typography>
                      <Typography variant="body1">
                        <p>
                          <strong>EPF2:</strong> <p> {EPF2}</p>
                        </p>
                      </Typography>
                    </Grid>
                  </Grid>
                </Grid>
              </Form>
            </Paper>
          </div>
        );
      }}
    </Formik>
  );
};
export default NewRegistrationReceipt;
