import Error from "../ErrorMessage.js";
import React from "react";
import { useRouter } from "next/router";
import useForm from "../utils/useForm";
import FRadioGroup from "../muiComponents/controls/SygefexMuiRadioGroup";
import Image from "material-ui-image";
import * as Yup from "yup";
import { createExaminerMutation } from "../queries&Mutations&Functions/Mutations";

import { TextField } from "material-ui-formik-components/TextField";
import { ErrorMessage, Formik, Form, Field } from "formik";
import {
  Grid,
  Typography,
  Paper,
  Button,
  LinearProgress,
  CircularProgress,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useMutation, useApolloClient } from "@apollo/react-hooks";
import { MuiPickersUtilsProvider, DatePicker } from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";

const useStyles = makeStyles({
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "50%",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },
  MuiFormControl: {
    root: {
      margin: "0rem",
    },
  },

  partTwo: {
    display: "grid",
    gridTemplateRows: "1fr 1fr ",
  },

  picStyled: {
    paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "40%",
  },
});

const validationSchema = Yup.object().shape({
  examiner1stName: Yup.string().required("Nom obligatoire"),
  examiner2ndName: Yup.string().required("Prénom obligatoire"),
  examinerOtherNames: Yup.string(),
  examinerCNI: Yup.string().required("Noms de la mere obligatoire"),
  examinerMatricule: Yup.string().required("Noms du pere obligatoire"),
  placeOfBirth: Yup.string().required("Lieu de naissance obligatoire"),

  examinerPhone: Yup.number().required("Numéro de portable obligatoire"),
  gender: Yup.string().required("Choix du sexe obligatoire"),
  dateOfBirth: Yup.date()
    .min(new Date("01-01-1900"))
    .max(new Date())
    .required("Date de naissance obligatoire"),
  examinerEmail: Yup.string()
    .email("Email invalide")
    .required("Email obligatoire"),
});

const Newexamineridates = () => {
  const classes = useStyles();
  const router = useRouter();
  const [state, setState] = useForm({
    image: "",
    dateOfBirth: new Date("2018-01-01T00:00"),
    // selectedDateString: new Date("2001-07-04T12:08:54"),
  });
  const initialValues = {
    examiner1stName: "",
    examiner2ndName: "",
    examinerOtherNames: "",
    examinerCNI: "",
    examinerMatricule: "",
    examinerEmail: "",
    examinerImage: "",
    examinerPhone: "",
    placeOfBirth: "",
    dateOfBirth: "",
    gender: "",
  };
  const { examinerImage } = state;
  const uploadFile = async (e) => {
    setState((state) => ({ ...state, examinerImage: "" }));
    const files = e.target.files;
    const data = new FormData();
    data.append("file", files[0]);
    data.append("upload_preset", "ineximages");
    const res = await fetch(
      "https://api.cloudinary.com/v1_1/inex/image/upload",
      {
        method: "POST",
        body: data,
      }
    );
    const file = await res.json();
    console.log(file);
    setState((state) => ({ ...state, examinerImage: file.secure_url }));
  };
  // const handleDateChange1 = (event) => {
  //   console.dir(event);
  //   const { value } = event.target;
  //   setState((state) => ({ ...state, selectedDate: value }));
  // };
  const handleChange = (e, date) => {
    setState((state) => ({
      ...state,
      dateOfBirth: date,
      // selectedDateString: moment(date).format("DD-MM-YYYY"),
    }));
  };

  console.log(examinerImage);
  const [createExaminer, { error, loadingMut }] = useMutation(
    createExaminerMutation
  );
  console.log(state);
  return (
    <Formik
      method="POST"
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={async (values, { setSubmitting, resetForm }) => {
        const res = await createExaminer({
          variables: {
            ...values,
            examinerImage: examinerImage,
          },
        });
        router.push({
          pathname: "/show/singleExaminer",
          query: {
            id: res.data.createExaminer.id,
          },
        });
        setTimeout(() => {
          console.log(JSON.stringify(values, null, 2));
          console.log(res);
          resetForm();
          setSubmitting(false);
        }, 200);
      }}
    >
      {({ submitForm, values, setFieldValue, isSubmitting }) => (
        <div className={classes.centerAll}>
          <Paper className={classes.pageStyled} elevation={13}>
            <Form aria-busy={isSubmitting}>
              {(isSubmitting || loadingMut) && <LinearProgress />}
              <Grid className={classes.centerAll} container>
                <Grid container className={classes.centerAll}>
                  <Grid item>
                    <Error error={error} />
                    <Typography
                      className={classes.titleStyled}
                      color="primary"
                      gutterBottom
                      variant="h5"
                      component="h6"
                    >
                      Renseignement Examinateur
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.centerAll} xs={12} sm={6}>
                    <Field
                      helperText={<ErrorMessage name="file" />}
                      component={TextField}
                      variant="outlined"
                      name="file"
                      type="file"
                      onChange={uploadFile}
                      disabled={isSubmitting}
                    />

                    <Field
                      helperText={<ErrorMessage name="examiner1stName" />}
                      component={TextField}
                      variant="outlined"
                      name="examiner1stName"
                      type="text"
                      label="Nom"
                      disabled={isSubmitting}
                    />
                    <Field
                      helperText={<ErrorMessage name="examiner2ndName" />}
                      component={TextField}
                      variant="outlined"
                      name="examiner2ndName"
                      type="text"
                      label="Prénom"
                      disabled={isSubmitting}
                    />
                    <Field
                      helperText={<ErrorMessage name="examinerOtherNames" />}
                      component={TextField}
                      variant="outlined"
                      name="examinerOtherNames"
                      type="text"
                      label="Autres Noms"
                      disabled={isSubmitting}
                    />
                    <Field
                      helperText={<ErrorMessage name="placeOfBirth" />}
                      component={TextField}
                      variant="outlined"
                      name="placeOfBirth"
                      type="text"
                      label="Lieu de Naissance"
                      disabled={isSubmitting}
                    />
                    <Field
                      helperText={<ErrorMessage name="examinerMatricule" />}
                      component={TextField}
                      variant="outlined"
                      name="examinerMatricule"
                      type="text"
                      label="Matricule"
                      disabled={isSubmitting}
                    />
                    <Field
                      helperText={<ErrorMessage name="examinerCNI" />}
                      component={TextField}
                      variant="outlined"
                      name="examinerCNI"
                      type="text"
                      label="Numéro de CNI"
                      disabled={isSubmitting}
                    />

                    <Field
                      helperText={<ErrorMessage name="examinerPhone" />}
                      component={TextField}
                      variant="outlined"
                      name="examinerPhone"
                      type="number"
                      label="Numéro de portable"
                      disabled={isSubmitting}
                    />
                  </Grid>

                  <Grid item className={classes.partTwo} xs={12} sm={6}>
                    <Field
                      helperText={<ErrorMessage name="examinerEmail" />}
                      component={TextField}
                      variant="outlined"
                      name="examinerEmail"
                      type="email"
                      label="Email"
                      disabled={isSubmitting}
                    />

                    <Field
                      helperText={<ErrorMessage name="Gender" />}
                      required
                      name="gender"
                      component={FRadioGroup}
                      variant="outlined"
                      label="Gender"
                      options={[
                        { value: "M", label: "Male" },
                        { value: "F", label: "Femele" },
                      ]}
                      groupProps={{ row: true }}
                    />
                    <MuiPickersUtilsProvider utils={MomentUtils}>
                      <DatePicker
                        helperText={<ErrorMessage name="dateOfBirth" />}
                        variant="inline"
                        inputVariant="outlined"
                        autoOk
                        margin="normal"
                        id="dateOfBirth"
                        name="dateOfBirth"
                        label="Birth Date"
                        format="DD MMMM YYYY"
                        value={state.dateOfBirth}
                        openTo="year"
                        disableFuture
                        onChange={(value) =>
                          setFieldValue("dateOfBirth", value._d)
                        }
                        // KeyboardButtonProps={{
                        //   "aria-label": "change date",
                        // }}
                      />
                    </MuiPickersUtilsProvider>
                    <div className={classes.picStyled}>
                      {!examinerImage ? (
                        <CircularProgress />
                      ) : (
                        <img
                          style={{
                            height: "15rem",
                            width: "15rem",
                            display: "grid",
                            backgroundSize: "contain",
                            backgroundPosition: "center",
                            placeItems: "center",
                            borderRadius: "0.5rem",
                          }}
                          src={examinerImage}
                          alt="Upload image"
                        />
                      )}
                    </div>

                    <Button disabled={isSubmitting} onClick={submitForm}>
                      {(isSubmitting || loadingMut) && <CircularProgress />}
                      {isSubmitting ? "Création en cours" : "Crée Examinateur"}
                    </Button>
                  </Grid>
                </Grid>
              </Grid>
            </Form>
          </Paper>
        </div>
      )}
    </Formik>
  );
};
export default Newexamineridates;
