import React from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import CssBaseline from "@material-ui/core/CssBaseline";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import HomeIcon from "@material-ui/icons/Home";
import ReceiptIcon from "@material-ui/icons/Receipt";
import NotificationsIcon from "@material-ui/icons/Notifications";
import DesktopWindowsIcon from "@material-ui/icons/DesktopWindows";
import SettingsIcon from "@material-ui/icons/Settings";
import IconButton from "@material-ui/core/IconButton";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import MenuIcon from "@material-ui/icons/Menu";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Sidebar from "./Sidebar";

const drawerWidth = 320;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    // [theme.breakpoints.up("sm")]: {
    //   width: `calc(100% - ${drawerWidth}px)`,
    //   marginLeft: drawerWidth,
    // },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

const items = [
  "divider",
  {
    url: "../../creates/login",
    name: "home",
    label: "Home",
    Icon: HomeIcon,
  },
  "divider",
  {
    // url: "../../creates/newRegion",
    name: "localization",
    label: "Localization",
    Icon: ReceiptIcon,
    items: [
      {
        // url: "../../creates/newRegion",
        name: "region",
        label: "Region",
        items: [
          { url: "../../creates/newRegion",  name: "createRegion",label: "Cree Region" },
          {
            url: "../../modify/modifyRegion",
            name: "modifierRegion",
            label: "Modifier Region",
          },
        ],
      },
      {
        // url: "../../creates/newDivision",
        name: "division",
        label: "Division",
        items: [
          { url: "../../creates/newDivision", label: "Cree Departement" },
          {
            url: "../../modify/modifyDivision",
            name: "modifierDivision",
            label: "Modifier Dept",
          },
        ],
      },
      {
        url: "../../creates/newSubDivision",
        name: "subDivision",
        label: "Sub Division",
        items: [
          { url: "../../creates/newRegion", label: "Cree Region" },
          {
            url: "../../modify/modifyRegion",
            name: "modifierRegion",
            label: "Modification",
          },
        ],
      },
      {
        url: "../../creates/newTown",
        name: "town",
        label: "Town",
        items: [
          { url: "../../creates/newRegion", label: "Cree Region" },
          {
            url: "../../modify/modifyRegion",
            name: "modifierRegion",
            label: "Modification",
          },
        ],
      },
    ],
  },
  "divider",
  {
    url: "../../creates/newExaminer",
    name: "localization",
    label: "Examinateur",
    Icon: ReceiptIcon,
    items: [
      {
        url: "../../creates/newExaminer",
        name: "examiner",
        label: "Renseignement",
        items: [
          {
            url: "../../creates/newExaminer",
            name: "createExaminer",
            label: "Cree Examinateur",
          },
          {
            url: "../../modify/modifyExaminer",
            name: "modifierExaminer",
            label: "Modifier Examinateur",
          },
        ],
      },
      {
        // url: "../../creates/newCenterExaminer",
        name: "registerExaminer",
        label: "Inscription",
        items: [
          {
            url: "../../creates/newCenterExaminer",
            name: "centerExaminer",
            label: "S'inscrire au Centre",
          },
          {
            url: "../../modify/modifyDivision",
            name: "modifierDivision",
            label: "Info Examinateur",
          },
          {
            url: "../../creates/newReport",
            name: "examinerReport",
            label: "Rapport ",
          },
        ],
      },
      {
        url: "../../creates/newReport",
        name: "examinerReport",
        label: "Rapport ",
        items: [
          { url: "../../creates/newRegion", label: "Cree Region" },
          {
            url: "../../modify/modifyRegion",
            name: "modifierRegion",
            label: "Modification",
          },
        ],
      },
      {
        url: "../../creates/newTown",
        name: "town",
        label: "Town",
        items: [
          { url: "../../creates/newRegion", label: "Cree Region" },
          {
            url: "../../modify/modifyRegion",
            name: "modifierRegion",
            label: "Modification",
          },
        ],
      },
    ],
  },
  "divider",
  {
    url: "../../creates/newCenter",
    name: "settings",
    label: "Settings",
    Icon: SettingsIcon,
    items: [
      { url: "../../creates/newCenter", name: "profile", label: "Profile" },
      { url: "../../creates/newCenter", name: "insurance", label: "Insurance" },
      "divider",
      {
        url: "../../creates/newCenter",
        name: "notifications",
        label: "Notifications",
        Icon: NotificationsIcon,
        items: [
          { url: "../../creates/newCenter", name: "email", label: "Email" },
          {
            url: "../../creates/newCenter",
            name: "desktop",
            label: "Desktop",
            Icon: DesktopWindowsIcon,
            items: [
              {
                url: "../../creates/newCenter",
                name: "schedule",
                label: "Schedule",
              },
              {
                url: "../../creates/newCenter",
                name: "frequency",
                label: "Frequency",
              },
            ],
          },
          { url: "../../creates/newCenter", name: "sms", label: "SMS" },
        ],
      },
    ],
  },
];

const LayoutDrawer = (props) => {
  const { window, children } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

const renderMenu = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      id={menuId}
      keepMounted
      transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      open={isMenuOpen}
      onClose={handleMenuClose}
    >
      <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
      <MenuItem onClick={handleMenuClose}>My account</MenuItem>
    </Menu>
  );


  const drawer = (
    <div style={{ backgroundColor: "#5e9fbc", color: "#fff" }}>
      <div className={classes.toolbar} />
      <Divider />
      <Sidebar items={items} />
    </div>
  );
  const container =
    window !== undefined ? () => window().document.body : undefined;
  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        style={{ backgroundColor: "#4a8ba8" }}
        // style={{ backgroundColor: "#044562" }}
        position="fixed"
        className={classes.appBar}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
          <Typography
            // style={{ color: "#fff" }}
            variant="h6"
            component="h5"
            noWrap
          >
            Sygefex-OBC
          </Typography>
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of urls. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <main className={classes.content}>{children}</main>
    </div>
  );
};

// LayoutDrawer.propTypes = {
//   /**
//    * Injected by the documentation to work in an iframe.
//    * You won't need it on your project.
//    */
//   window: PropTypes.func,
// };

export default LayoutDrawer;
