import React, { useState, useEffect } from "react";
import { useApolloClient } from "@apollo/react-hooks";
import { MiniStyledPage } from "../../styles/StyledPage";
import { format } from "date-fns";
import ResultDetails from "./ResultDetails";
import { getCandidateResultsQuery } from "../../queries&Mutations&Functions/Queries";
import { ErrorMessage, Formik, Form, Field } from "formik";
import {
  Grid,
  Typography,
  Paper,
  Button,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { ButtonStyled, StyledButton, StyledForm } from "../../utils/FormInputs";
import styled from "styled-components";
import {
  roundFloatNumber,
  calcCandTotalScore,
  calcCandTotalCoeff,
  calcCandAve,
} from "../../queries&Mutations&Functions/Functions";



const useStyles = makeStyles({
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "50%",
    borderRadius: "0.8rem",
    borderBottom: "0.4rem solid #c89666",
    borderTop: "0.4rem solid #829079",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },
  MuiFormControl: {
    root: {
      margin: "0rem",
    },
  },

  partTwo: {
    display: "grid",
    gridTemplateRows: "1fr 1fr ",
  },

  picStyled: {
    paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "40%",
  },

  resultsTitle: {
    display: "grid",
    gridTemplateColumns:"4fr 0.8fr 1.5fr 1.5fr",
    gridGap:"0.5rem",
    placeItems: "center",
  },

  totals: {
    display: "grid",
    gridTemplateColumns:"1fr 1fr 1fr",
    gridGap:"0.5rem",
    placeItems: "center",
  },
});


const CandDetailedResultsHook = ({ id }) => {
  const classes = useStyles()
  const [state, setState] = useState({ results: {} });
  const client = useApolloClient();

  const loadCenterRegistrationInfo = async () => {
    console.log(`running the difficult query: ${id}`);
    const { data } = await client.query({
      query: getCandidateResultsQuery,
      variables: { id },
    });
    console.log(data);
    const getAllCandResults = data.registration;
    console.log(getAllCandResults);
    // const { registration, center, examSession } = getCenterExamSession;
    // const { exam, session } = { ...examSession };

    setState((prev) => ({ ...prev, results: getAllCandResults }));
  };

  useEffect(() => {
    loadCenterRegistrationInfo();
  }, []);

  console.log(state.results);
  const {
    candidate,
    centerExamSessionSpecialty,
    candRegistrationNumber,
    scores,
  } = state.results ;

  const { centerExamSession, specialty } = { ...centerExamSessionSpecialty };

  const { examSession, center } = { ...centerExamSession };
  const { exam, session } = { ...examSession };
  const { centerName } = { ...center };
  const { examName } = { ...exam };
  const { sessionName } = { ...session };
  const { specialtyName } = { ...specialty };

  const {
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    placeOfBirth,
    dateOfBirth,
    dadName,
    momName,
    gender,
  } = {...candidate} ;

  const initialValues = {
    candCode: "",
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    placeOfBirth: "",
    dateOfBirth: "",
    candExamSecretCode: "",
  };
  return (
    <Formik method="GET" initialValues={initialValues}>
      {({ isSubmitting }) => (
        <div className={classes.centerAll}>
          <Paper className={classes.pageStyled} elevation={13}>
            <Form aria-busy={isSubmitting}>
              <Grid className={classes.centerAll} container>
                    <Grid container className={classes.centerAll}>
                      <Grid item>
                        <Typography
                          className={classes.titleStyled}
                          color="primary"
                          gutterBottom
                          variant="h6"
                          component="h6"
                        > Resultat Du: {exam && examName}, Session:
                          {session && sessionName}, Pour:-
                          {candidate && cand1stName}
                          {candidate && cand2ndName}
                        </Typography>
                      </Grid>
                    </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.centerAll} xs={12} sm={6}> 
              
                  <Typography color="primary" variant="body1">
                      <strong>Centre d'Examen:</strong>
                      <span> 
                          <strong>
                         {center && centerName}
                          </strong>
                          </span>
                    </Typography>

                      <div  style={{diaplay: "flex", }}  >
                        
                
                <Typography color="primary" variant="body1">
                      <strong>Examen:</strong>
                      <span> 
                          <strong>
                         {exam && examName}
                          </strong>
                          </span>
                    </Typography>
                    
                <Typography color="primary" variant="body1">
                      <strong>Session:</strong>
                      <span> 
                          <strong>
                          {session && sessionName}
                          </strong>
                          </span>
                    </Typography>
                    </div>
                    <Typography color="primary" variant="body1">
                      <strong>Numero d'Inscription:</strong>
                      <span> 
                          <strong>
                          {candRegistrationNumber}
                          </strong>
                          </span>
                    </Typography>

                    <Typography color="primary" variant="body1">
                      <strong>Specialization:</strong>
                      <span> 
                          <strong>
                          specialty && specialtyName}
                          </strong>
                          </span>
                    </Typography>
                   </Grid>
                 </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.centerAll} xs={12} sm={4}> 
                
                            <img
                              // src={candidate && image}
                              alt={candidate && cand1stName}
                            />
                  </Grid>
                  <Grid item className={classes.centerAll} xs={12} sm={8}> 
        

                <Typography color="primary" variant="body1">
                      <strong>Nom:</strong>
                      <span> 
                          <strong>
                         {candidate && cand1stName}
                          </strong>
                          </span>
                    </Typography>
                <Typography color="primary" variant="body1">
                      <strong>Prenom:</strong>
                      <span> 
                          <strong>
                         {candidate && cand2ndName}
                          </strong>
                          </span>
                    </Typography>
                <Typography color="primary" variant="body1">
                      <strong>Autres:</strong>
                      <span> 
                          <strong>
                         {candidate && cand3rdName}
                          </strong>
                          </span>
                    </Typography>
                    <strong>Naissance: </strong>
                    <hr />
                <Typography color="primary" variant="body1">
                      <strong>Lieu:</strong>
                      <span> 
                          <strong>
                         {candidate && placeOfBirth}
                          </strong>
                          </span>
                    </Typography>
                <Typography color="primary" variant="body1">
                      <strong>Date:</strong>
                      <span> 
                          <strong>
                          {format(candidate && dateOfBirth, "d MMMM, YYYY ")}
                          </strong>
                          </span>
                    </Typography>
                <Typography color="primary" variant="body1">
                      <strong>Sexe:</strong>
                      <span> 
                          <strong>
                         {candidate && gender}
                          </strong>
                          </span>
                    </Typography>
                    <strong>Parents: </strong>
                    <hr />
                <Typography color="primary" variant="body1">
                      <strong>Pere:</strong>
                      <span> 
                          <strong>
                         {candidate && dadName}
                          </strong>
                          </span>
                    </Typography>
                <Typography color="primary" variant="body1">
                      <strong>Mere:</strong>
                      <span> 
                          <strong>
                         {candidate && momName}
                          </strong>
                          </span>
                    </Typography>
                <Typography color="primary" variant="body1">
                      <strong>Email:</strong>
                      <span> 
                          <strong>
                         {candidate && email}
                          </strong>
                          </span>
                    </Typography>
                     
                  </Grid>
                </Grid>
                 <Grid container spacing={6}>
                    <Grid item className={classes.resultsTitle} xs={12}> 
                      




                    
                      <stromg>Matiere</stromg>
                      <stromg>Coeff</stromg>
                      <stromg>Moyenne</stromg>
                      <strong>Total</strong>
                      
                    </Grid>
                  </Grid>
                <Grid container spacing={6}>
                    <Grid item xs={12}> 
                        {scores &&
                          scores.map((item) => (
                            <ResultDetails key={item.id} score={item} />
                          ))}
                    </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.totals} xs={12} > 
            
                <Typography color="primary" variant="body1">
                      <strong>Moyenne:</strong>
                      <span> 
                          <strong>
                            {scores && roundFloatNumber(calcCandAve(scores), 4)}
                          </strong>
                          </span>
                    </Typography>

                <Typography color="primary" variant="body1">
                      <strong>Somme Coeffs:</strong>
                      <span> 
                          <strong>
                            {scores &&
                              roundFloatNumber(calcCandTotalCoeff(scores), 4)}
                          </strong>
                          </span>
                    </Typography>

                <Typography color="primary" variant="body1">
                      <strong>Totale Matieres:</strong>
                      <span> 
                          <strong>
                            {scores &&
                              roundFloatNumber(calcCandTotalScore(scores), 4)}
                          </strong>
                          </span>
                    </Typography>

                  </Grid>
                </Grid>
             </Grid>
            </Form>
          </Paper>
        </div>
        )
      }
    </Formik>
  );
};

export default CandDetailedResultsHook;
