import React from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import CssBaseline from "@material-ui/core/CssBaseline";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import MenuItem from "@material-ui/core/MenuItem";
import MenuList from "@material-ui/core/MenuList";
import IconButton from "@material-ui/core/IconButton";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import MailIcon from "@material-ui/icons/Mail";
import MenuIcon from "@material-ui/icons/Menu";
import Toolbar from "@material-ui/core/Toolbar";
import Link from "next/link";
import Typography from "@material-ui/core/Typography";
import { makeStyles, useTheme } from "@material-ui/core/styles";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    // [theme.breakpoints.up("sm")]: {
    //   width: `calc(100% - ${drawerWidth}px)`,
    //   marginLeft: drawerWidth,
    // },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

const localization = [
 { link:"../../creates/newRegion", title:"Region"},
 { link:"../../creates/newDivision", title:"Departement"},
 { link:"../../creates/newSubDivision", title:"Arrondissement"},
 { link:"../../creates/newTown", title:"Ville"},
 { link:"../../creates/newCenter", title:"Centre"},
  
];

const menuDetails = [
 {  menuTitle:"Region",
 details:[
   {link:"../../creates/newRegion", title:"New Region" },
   {link:"../../modify/modifyRegion", title:"Edit Region" },
  ]},
 {  menuTitle:"Division",
 details:[
   {link:"../../creates/DivisionRegion", title:"New Departement" },
   {link:"../../modify/DivisionRegion", title:"Edit Departement" },
  ]},
 {  menuTitle:"Arrondissement",
 details:[
   {link:"../../creates/newSubDivision", title:"New Arrondissement" },
   {link:"../../modify/modifySubDivision", title:"Edit Arrondissement" },
  ]},
 {  menuTitle:"Ville",
 details:[
   {link:"../../creates/newTown", title:"New Ville" },
   {link:"../../modify/modifyTown", title:"Edit Ville" },
  ]},
 {  menuTitle:"Centre",
 details:[
   {link:"../../creates/newCenter", title:"New Centre" },
   {link:"../../modify/modifyCenter", title:"Edit Centre" },
  ]},
 
];


const LayoutDrawer = (props) => {
  const { window, children } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div>
      <div className={classes.toolbar} />
      <Divider />
      <Typography variant="body1" component="h6">
        Localization
      </Typography>
      <MenuList>
        {localization.map(({link, title}) => (
          <Link href={link} passHref>
            <MenuItem  key={title}>
              {title}
            </MenuItem>
          </Link>
        ))}
      </MenuList>
      <Divider />
     
    </div>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        style={{ backgroundColor: "#044562" }}
        position="fixed"
        className={classes.appBar}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap>
            Sygefex-OBC
          </Typography>
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === "rtl" ? "right" : "left"}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <main className={classes.content}>{children}</main>
    </div>
  );
};

// LayoutDrawer.propTypes = {
//   /**
//    * Injected by the documentation to work in an iframe.
//    * You won't need it on your project.
//    */
//   window: PropTypes.func,
// };

export default LayoutDrawer;
