import React,{useState, useEffect} from "react";
import Error from "../ErrorMessage.js";
import { ErrorMessage, Formik, Form, Field } from "formik";
import { Select } from "material-ui-formik-components/Select";
import {useApolloClient, useMutation} from "@apollo/react-hooks"

import {
  Grid,
  Typography,
  Paper,
  Button,
  LinearProgress,
  CircularProgress,
} from "@material-ui/core";
import {
  getObjectFromID,
  removeTypename,
} from "../queries&Mutations&Functions/Functions";

import { makeStyles } from "@material-ui/core/styles";
import * as Yup from "yup";
import Notification from "../utils/Notification";
import { createPhaseRankMutation } from "../queries&Mutations&Functions/Mutations";
import { getAllPhasesQuery,getAllRanksQuery   } from "../queries&Mutations&Functions/Queries";

const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    // fontSize: 100,
  },
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    maxWidth: "30%",
  },
  listStyled: {
    display: "grid",
    placeItems: "center",
    listStyleType: "none",
    margin: 0,
    padding: 0,
    paddingTop: "0.1rem",
  },
  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  allControls: {
    display: "grid",
    placeItems: "center",
    paddingTop: "0.2rem",
    border: "0.05rem solid #1254ac",
    // width: "20vw",
    borderRadius: "0.5rem",
    // marginTop: "2rem",
  },
  centerAll: {
    display: "grid",
    placeItems: "center",
  },
});

const validationSchema = Yup.object().shape({
  phaseName: Yup.string().required("Libelle phase Obligatoire"),

  phaseCode: Yup.string().required("Code phase Obligatoire"),
});

const Login = () => {

  const client = useApolloClient()



  const classes = useStyles();
  const initialValues = {
    phaseName: "",
    phaseCode: "",
  };

const [state, setState]= useState({phases:[], ranks:[]})
  
  const [notify, setNotify] = useState({
    isOpen: false,
    message: "",
    type: "",
  });
  
const loadPhasesData=async()=>{
  const {data}=await client.query({
    query:getAllPhasesQuery,
  })

  const allPhases = data && data.phases;
  console.log(allPhases);
  const refinedPhase = allPhases && removeTypename(allPhases);
  setState((prev)=>({...prev, phases:refinedPhase}))
}

  const getPhasesOptions =
    state.phases &&
    state.phases.map((item) => ({
      value: item.id,
      label: item.phaseName,
    }));
useEffect(()=>{
loadPhasesData()
}, [])



const loadRanksData=async()=>{
  const {data}=await client.query({
    query:getAllRanksQuery,
  })
const AllRanks = data && data.ranks;
  const refinedRank = AllRanks && removeTypename(AllRanks);
  setState((prev)=>({...prev, ranks:refinedRank}))
}

  const getRanksOptions =
    state.ranks &&
    state.ranks.map((item) => ({
      value: item.id,
      label: item.rankName,
    }));
useEffect(()=>{
loadRanksData()
}, [])



  const [createPhaseRank, { loading, Error: errMut }] = useMutation(
    createPhaseRankMutation
  );

  return (
    <Formik
      method="POST"
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={async (values, { setSubmitting, resetForm }) => {
         const res = await createPhaseRank({
          variables: {
            ...values,
            phase: state.phases && getObjectFromID(values.phase.value),
            rank: state.ranks && getObjectFromID(values.rank.value),
          },
        });
        setTimeout(() => {
          console.log(JSON.stringify(values, null, 2));
          console.log(res);
setNotify({
            isOpen: true,
            message: "Opération réussie",
            type: "success",
          });
          resetForm(true);
          setSubmitting(false);
        }, 200);
      }}
    >
      {({ submitForm, isSubmitting }) => {
        return (
          <div className={classes.centerAll}>
            <Paper className={classes.pageStyled} elevation={3}>
              <Form aria-busy={isSubmitting}>
                {(isSubmitting || loading) && <LinearProgress />}
                <Grid className={classes.centerAll} container>
                  <Grid container className={classes.centerAll}>
                    <Grid item>
                      <Typography
                        className={classes.titleStyled}
                        color="primary"
                        gutterBottom
                        variant="body2"
                        component="h6"
                      >
                       Postes des Phases de l'Examen
                      </Typography>
                    </Grid>
                  </Grid>
                  <Grid item className={classes.centerAll}>
                    <Field
                      name="phase"
                      component={Select}
                      type="text"
                      fullWidth
                      label={"La Phase de l'examen"}
                      variant="outlined"
                        options={getPhasesOptions}
                      disabled={isSubmitting || loading}
                      helpertext={<ErrorMessage name="phase" />}
                    />
                    <Field
                      name="rank"
                      component={Select}
                      type="text"
                      fullWidth
                      placeholder={"Poste de responsabilité tenu"}
                      variant="outlined"
                      disabled={isSubmitting || loading}
                        options={getRanksOptions}

                      helpertext={<ErrorMessage name="rank" />}
                    />
                <Notification notify={notify} setNotify={setNotify} />

                    <Button disabled={isSubmitting} onClick={submitForm}>
                      {(isSubmitting || loading) && <CircularProgress />}
                      {isSubmitting || loading
                        ? "Création en cours"
                        : "Poste a la Phase"}
                    </Button>
                  </Grid>
                </Grid>
              </Form>
            </Paper>
          </div>
        );
      }}
    </Formik>
  );
};
export default Login;
