import React from "react";
import Select from "react-select";
import { useField, Field } from "formik";

import styled, { keyframes } from "styled-components";

const loading = keyframes`
  from {
    background-position: 0 0;
    /* rotate: 0; */
  }

  to {
    background-position: 100% 100%;
    /* rotate: 360deg; */
  }
`;

const customStyle = {
  control: (provided) => ({
    ...provided,
    fontFamily: "Roboto Slab",
    border: "0 solid black",
    borderBottom: "0.9px solid #0780b7",
    fontSize: "1.6rem",
    paddingTop: "1rem",
    outline: "none",
    backgroundColor: "white",
    color: "#000",
    borderRadius: "0",
    boxShadow: "none",
    alignItems: "center",
    "&:hover": {
      borderBottom: " 0.2rem solid #d4351c",
      borderRadius: "0",
    }, // border style on hover
    "&:focus": {
      borderBottom: " 0.2rem solid #d4351c",
      borderRadius: "0",
    }, // border style on hover
  }),
  option: (provided) => ({
    ...provided,
    textAlign: "left",
    fontSize: "1.5rem",
  }),
};

const StyledInput = styled.input`
  padding-top: 1rem;
  /* display:block; */
  margin-top: 0.4rem;
  font-size: 1.5rem;
  border-style: none;
  width: 100%;
  outline: none;
  border-bottom: 0.09rem solid black;
  z-index: 10;
  font-family: "Roboto Slab";

  &:valid + label {
    top: -0.25rem;
    font-size: 1.1rem;
    color: ${(props) => props.theme.britishBlue};
  }
  &:focus,
  &active {
    outline: none;
    border-bottom: 0.2rem solid ${(props) => props.theme.britishRed};
  }
  &:focus + label {
    top: -0.25rem;
    font-size: 1.1rem;
    color: #aaa;
  }
`;

const InputGroup = styled.div`
  position: relative;
`;

const AllRadios = styled.div`
  position: relative;
  margin: 0.5rem;
  label {
    border: 0;
    width: 2rem;
    height: 2rem;
  }
`;

const StyledLabel = styled.label`
  font-size: 1.5rem;
  border-style: none;
  position: absolute;
  left: 1rem;
  top: 1.5rem;
  color: grey;
  pointer-events: none;
  transition: all 1s ease-out;
  font-family: "Roboto Slab";
  &:focus {
    outline: none;
  }
`;

const StyledSelect = styled.select`
  margin-top: 1rem;
  font-size: 1.2rem;
  border-style: none;
  border-bottom: 0.09rem solid black;
  option {
    background: white;
    font-weight: 400;
    display: flex;
    white-space: pre;
    min-height: 20px;
    padding: 0px 2px 1px;
    background: black;
    border-radius: 0.5rem;
  }
  &:focus {
    outline: none;
    border-color: ${(props) => props.theme.britishRed};
  }
`;

const StyledErrorMessage = styled.div`
  padding-top: 0.2rem;
  font-size: 1.2rem;
  color: ${(props) => props.theme.britishRed};
  /* padding-left: 0.5rem; */
`;

const RadioStyled = styled.div`
  display: inline-block;
  input,
  label :hover {
    cursor: pointer;
    background: black;
  }

  input[type="radio"] {
    margin-top: 0.5rem;
    border: 0;
    width: 2rem;
    height: 2rem;
    margin: 0 0.7rem;

    background: black;
  }
`;

const StyledButton = styled.button`
  color: black;
  border: 0;
  border-radius: 0.5rem;
  font-size: 2.5rem;
  font-weight: 300;
  line-height: 1.5rem;
  /* box-shadow: ${(props) => props.theme.bs2}; */
  cursor: pointer;
`;

const ButtonStyled = styled.div`
  padding-bottom: 0.5rem;
  padding-top: 1rem;
  button {
    border-radius: 0.6rem;
    font-size: 1.5rem;
    font-weight: bold;
    box-shadow: ${(props) => props.theme.formBS};
    border: 0.05rem solid black;
    color: black;
    background: ${(props) => props.theme.pureWhite};
    cursor: pointer;
    padding: 0.8rem;
    outline: none;
    &:hover {
      color: black;
      background-color: ${(props) => props.theme.pureWhite};
    }
  }

  a {
    text-decoration: none;
  }
`;

const StyledForm = styled.div`
  margin: 0 3rem;
  padding: 0.6rem;
  font-size: 1.7rem;
  border-radius: 0.9rem;
  border: 0.001rem solid black;
  border-top: 1rem solid black;
  border-bottom: 1rem solid ${(props) => props.theme.tableRed};
  box-shadow: ${(props) => props.theme.formBS};

  min-width: 20rem;

  &[disabled] {
    opacity: 0.3;
  }
  &::before {
    height: 0.3rem;
    content: "";
    display: block;
    background-image: linear-gradient(
      to right,
      #0780b7 0%,
      #e2b04a 20%,
      #0780b7 100%
    );
    /* background-image: linear-gradient(to right, #ff3019 0%, #e2b04a 50%, #ff3019 100%); */
  }
  &[aria-busy="true"]::before {
    background-size: 50% auto;
    animation: ${loading} 0.5s linear infinite;
  }
`;

const SygexInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <InputGroup>
      <StyledInput className="text-input" {...field} {...props} required />
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      {meta.touched && meta.error ? (
        <StyledErrorMessage>{meta.error}</StyledErrorMessage>
      ) : null}
    </InputGroup>
  );
};

const SygefexSelect = ({ theme, ...props }) => {
  const customTheme = (theme) => ({
    ...theme,
    colors: {
      ...theme.colors,
      primary25: "#0780b7",
      primary: "#d17f05",
    },
  });
  return (
    <Select theme={customTheme} styles={customStyle} {...props} isSearchable />
  );
};

const SygefexRadio = ({ label, options, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <AllRadios>
      <label>{label}</label>
      <Field label={label} {...field} {...props}>
        {({ field }) => {
          return options.map((item) => (
            <RadioStyled>
              <input
                {...field}
                type="radio"
                id={item.value}
                value={item.value}
                checked={field.value === item.value}
              />
              <label htmlFor={props.id || item.value}>{item.key}</label>
            </RadioStyled>
          ));
        }}
      </Field>
      {meta.touched && meta.error ? (
        <StyledErrorMessage>{meta.error}</StyledErrorMessage>
      ) : null}
    </AllRadios>
  );
};

export {
  SygefexSelect,
  SygefexRadio,
  StyledLabel,
  StyledButton,
  ButtonStyled,
  SygexInput,
  StyledForm,
};

