import React, { useEffect, useState } from "react";
import MaterialTable from "material-table";
import { Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient, useMutation } from "@apollo/react-hooks";
import { getAllRegionsQuery } from "../queries&Mutations&Functions/Queries";
import tableIcons from "../utils/icons/tableIcons";
import AddPopup from "../utils/AddPopup";
import UpdatePopup from "../utils/UpdatePopup";
import ConfirmDialog from "../utils/ConfirmDialog";
import { deleteRegionMutation } from "../queries&Mutations&Functions/Mutations";
import { useConfirm } from "material-ui-confirm";

import {
  Edit as EditIcon,
  Add as AddIcon,
  Delete as DeleteIcon,
} from "@material-ui/icons";

import NewRegionMui from "./NewRegionMui";
import UpdateRegion from "./UpdateRegion";

const useStyles = makeStyles({
  stickyActionsColumn: {
    "& table:first-child": {
      "& tr": {
        "& td:first-child, th:first-child": {
          backgroundColor: "#f5f5f5",
          position: "sticky",
          left: 0,
          zIndex: 10,
        },
        "& th:first-child": {
          zIndex: 10,
        },
      },
    },
  },
  stickyHeader: {
    "& table:first-child": {
      "& tr": {
        "& th": {
          backgroundColor: "#5aa193",
          position: "sticky",
          top: "4rem",
          zIndex: 10,
        },
        "& th": {
          zIndex: 10,
        },
      },
    },
  },
});

const RegionList = () => {
  const confirm = useConfirm();

  const client = useApolloClient();
  const [isAddPopupOpen, setIsAddPopupOpen] = useState(false);
  const [updatePopupState, setUpdatePopupState] = useState({
    isOpen: false,
    id: "",
  });
  const [deleteConfirmDialog, setDeleteConfirmDialog] = useState({
    isOpen: false,
    id: "",
    title: "",
    subtitle: "",
  });

  const [state, setState] = useState({
    columns: [
      {
        field: "regName",
        title: "Region Name",
      },
      {
        field: "regCode",
        title: "Region Code",
      },
      { field: "id", title: "ID" },
    ],
    data: [],
  });

  const loadRegionData = async () => {
    const { error, data } = await client.query({
      query: getAllRegionsQuery,
    });
    {
      error && <Error error={error} />;
    }
    const { regions } = { ...data };
    console.log(regions);
    console.log(regions);
    setState((prev) => ({ ...prev, data: regions }));
  };

  useEffect(() => {
    loadRegionData();
  }, []);

  const handleAddPopupClose = () => {
    setIsAddPopupOpen(false);
  };

  const handleUpdatePopupClose = () => {
    setUpdatePopupState((prev) => ({
      ...prev,
      isOpen: false,
    }));
  };
  const handleDeleteConfirmDialog = () => {
    setDeleteConfirmDialog((prev) => ({
      ...prev,
      isOpen: false,
    }));
  };
  const handleDelete = (item) => {
    confirm({
      description: `This will permanently delete ${rowData.id}.`,
    }).then(() => deleteRegion(rowData.id));
  };

  const [deleteRegion, { loading }] = useMutation(deleteRegionMutation, {
    variables: { id: deleteConfirmDialog.id },
    // update: updateCache(),
  });
  const actions = [
    {
      icon: () => <AddIcon />,
      tooltip: "Add User",
      isFreeAction: true,
      onClick: () => {
        setIsAddPopupOpen(true);
      },
    },
    {
      icon: () => <EditIcon />,
      tooltip: "Edit User",
      onClick: (event, rowData) => {
        setUpdatePopupState({ isOpen: true, id: rowData.id });
        // console.log(rowData.id);
      },
    },
    {
      icon: () => <DeleteIcon />,
      tooltip: "Delete User",
      onClick:(event,rowData)=>{
         handleDelete(rowData)
      }
      // onClick: (event, rowData) =>
      //   setDeleteConfirmDialog({
      //     // console.log("just starting the delete process");
      //     id: rowData.id,
      //     isOpen: true,
      //     title: "Etes-vous sur de suprimer cette inofrmation",
      //     subtitle:
      //       "Une fois supprimées, les informations seront perdues a jamais",
      //     onConfirm: () => {
      //       deleteRegion(rowData.id);
      //       console.log("just after the delete process");
      //     },
      //   }),
    },
  ];
  console.log({ deleteConfirmDialog });
  return (
    <Paper style={{ marginTop: "2rem" }}>
      <MaterialTable
        icons={tableIcons}
        title="Liste de Régions"
        columns={state.columns}
        data={state.data}
        actions={actions}
        stickyHeader
        style={{ position: "sticky", top: 0 }}
        icons={tableIcons}
      />
      <AddPopup
        title="Nouvelle Région"
        isOpen={isAddPopupOpen}
        onClose={handleAddPopupClose}
      >
        <NewRegionMui />
      </AddPopup>
      <ConfirmDialog
        title={deleteConfirmDialog.title}
        subTitle={deleteConfirmDialog.subtitle}
        isOpen={deleteConfirmDialog.isOpen}
        onClose={handleDeleteConfirmDialog}
      />

      <UpdatePopup
        // title={updatePopupState.id}
        isOpen={updatePopupState.isOpen}
        onClose={handleUpdatePopupClose}
      >
        <UpdateRegion id={updatePopupState.id} />
      </UpdatePopup>
    </Paper>
  );
};
export default RegionList;
