import React, { useState, useEffect } from "react";
import { useApolloClient } from "@apollo/react-hooks";
import { format } from "date-fns";
import ResultDetails from "./ResultDetails";
import { getCandidateResultsQuery } from "../../queries&Mutations&Functions/Queries";
import { Formik, Form } from "formik";
import { Grid, Typography, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  roundFloatNumber,
  calcCandTotalScore,
  calcCandTotalCoeff,
  calcCandAve,
} from "../../queries&Mutations&Functions/Functions";

const useStyles = makeStyles({
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "50%",
    borderRadius: "0.8rem",
    borderBottom: "0.4rem solid #c89666",
    borderTop: "0.4rem solid #829079",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },
  MuiFormControl: {
    root: {
      margin: "0rem",
    },
  },

  picStyled: {
    paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "40%",
  },

  generalHeading: {
    display: "grid",
    placeItems: "left",
    // width: "100%",
  },

  score: {
    backgroundColor: "#b9925e",
  },

  resultsTitle: {
    display: "grid",
    gridTemplateColumns: "4fr 0.3fr 1.5fr 1.5fr",
    // gridGap: "0.5rem",
    borderTopLeftRadius: "0.5rem",
    borderTopRightRadius: "0.5rem",
    placeItems: "center",
    backgroundColor: "#829079",
    color: "#ede6b9",
  },

  totals: {
    borderBottomLeftRadius: "0.5rem",
    borderBottomRightRadius: "0.5rem",
    display: "grid",
    gridTemplateColumns: "1fr 1fr 1fr",
    gridGap: "0.5rem",
    placeItems: "center",
    backgroundColor: "#829079",
    color: "#ede6b9",
  },
});

const CandDetailedResultsHook = ({ id }) => {
  const classes = useStyles();
  const [state, setState] = useState({ results: {} });
  const client = useApolloClient();

  const loadCenterRegistrationInfo = async () => {
    console.log(`running the difficult query: ${id}`);
    const { data } = await client.query({
      query: getCandidateResultsQuery,
      variables: { id },
    });
    console.log(data);
    const getAllCandResults = data.registration;
    console.log(getAllCandResults);
    // const { registration, center, examSession } = getCenterExamSession;
    // const { exam, session } = { ...examSession };

    setState((prev) => ({ ...prev, results: getAllCandResults }));
  };

  useEffect(() => {
    loadCenterRegistrationInfo();
  }, []);

  console.log(state.results);
  const {
    candidate,
    centerExamSessionSpecialty,
    candRegistrationNumber,
    scores,
  } = state.results;

  const { centerExamSession, specialty } = { ...centerExamSessionSpecialty };

  const { examSession, center } = { ...centerExamSession };
  const { exam, session } = { ...examSession };
  const { centerName } = { ...center };
  const { examName } = { ...exam };
  const { sessionName } = { ...session };
  const { specialtyName } = { ...specialty };

  const {
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    placeOfBirth,
    dateOfBirth,
    dadName,
    momName,
    gender,
  } = { ...candidate };

  const initialValues = {
    candCode: "",
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    placeOfBirth: "",
    dateOfBirth: "",
    candExamSecretCode: "",
  };
  return (
    <Formik method="GET" initialValues={initialValues}>
      {({ isSubmitting }) => (
        <div className={classes.centerAll}>
          <Paper className={classes.pageStyled} elevation={13}>
            <Form aria-busy={isSubmitting}>
              <Grid className={classes.centerAll} container>
                <Grid container className={classes.centerAll}>
                  <Grid item>
                    <Typography
                      className={classes.titleStyled}
                      color="primary"
                      gutterBottom
                      variant="body1"
                      component="h6"
                    >
                      <strong>
                        Resultat du: {exam && examName}, Session:
                        {session && sessionName}
                      </strong>
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item xs={12}>
                    <Typography color="primary" variant="body1">
                      <strong>Centre d'Examen:</strong>
                      <span>{center && centerName}</span>
                    </Typography>
                </Grid>
                    <div item
                      style={{
                        display: "flex"}}
                    >
                      <Typography color="primary" variant="body1">
                        <strong>Examen:</strong>
                        <span>{exam && examName}</span>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <strong>Session:</strong>
                        <span>{session && sessionName}</span>
                      </Typography>
                  </div>
                    <Typography color="primary" variant="body1">
                      <strong> Numero d'Inscription:</strong>
                      <span>{candRegistrationNumber}</span>
                    </Typography>

                    <Typography color="primary" variant="body1">
                      <strong>Specialization:</strong>
                      <span>{specialty && specialtyName}</span>
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.centerAll} xs={12} sm={4}>
                    <img
                      // src={candidate && image}
                      alt={candidate && cand1stName}
                    />
                  </Grid>
                  <Grid item className={classes.centerAll} xs={12} sm={8}>
                    <Typography color="primary" variant="body1">
                      <strong>Nom:</strong>
                      <span>
                        <strong>{candidate && cand1stName}</strong>
                      </span>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <strong>Prenom:</strong>
                      <span>
                        <strong>{candidate && cand2ndName}</strong>
                      </span>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <strong>Autres:</strong>
                      <span>
                        <strong>{candidate && cand3rdName}</strong>
                      </span>
                    </Typography>
                    <strong>Naissance: </strong>
                    <hr />
                    <Typography color="primary" variant="body1">
                      <strong>Lieu:</strong>
                      <span>
                        <strong>{candidate && placeOfBirth}</strong>
                      </span>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <strong>Date:</strong>
                      <span>
                        <strong>
                          {format(candidate && dateOfBirth, "d MMMM, YYYY ")}
                        </strong>
                      </span>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <strong>Sexe:</strong>
                      <span>
                        <strong>{candidate && gender}</strong>
                      </span>
                    </Typography>
                    <strong>Parents: </strong>
                    <hr />
                    <Typography color="primary" variant="body1">
                      <strong>Pere:</strong>
                      <span>
                        <strong>{candidate && dadName}</strong>
                      </span>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <strong>Mere:</strong>
                      <span>
                        <strong>{candidate && momName}</strong>
                      </span>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <strong>Email:</strong>
                      <span>
                        <strong>{candidate && email}</strong>
                      </span>
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.resultsTitle} xs={12}>
                    <Typography variant="body1">
                      <strong>Matiere</strong>
                    </Typography>

                    <Typography variant="body1">
                      <strong>Coeff</strong>
                    </Typography>

                    <Typography variant="body1">
                      <strong>Moyenne</strong>
                    </Typography>

                    <Typography variant="body1">
                      <strong>Total</strong>
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item xs={12}>
                    {scores &&
                      scores.map((item) => (
                        <ResultDetails key={item.id} score={item} />
                      ))}
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.totals} xs={12}>
                    <Typography variant="body1">
                      <strong>Moyenne:</strong>
                      <span>
                        <strong>
                          {scores && roundFloatNumber(calcCandAve(scores), 3)}
                        </strong>
                      </span>
                    </Typography>

                    <Typography variant="body1">
                      <strong>Somme Coeffs:</strong>
                      <span>
                        <strong>
                          {scores &&
                            roundFloatNumber(calcCandTotalCoeff(scores), 3)}
                        </strong>
                      </span>
                    </Typography>

                    <Typography variant="body1">
                      <strong>Totale Matieres:</strong>
                      <span>
                        <strong>
                          {scores &&
                            roundFloatNumber(calcCandTotalScore(scores), 3)}
                        </strong>
                      </span>
                    </Typography>
                  </Grid>
                </Grid>
            </Form>
          </Paper>
        </div>
      )}
    </Formik>
  );
};

export default CandDetailedResultsHook;
