  //   placeOfBirth,
  //   dateOfBirth,
  //   dadName,
  //   momName,
  //   gender,
  // } = candidate ;

  const initialValues = {
    candCode: "",
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    placeOfBirth: "",
    dateOfBirth: "",
    candExamSecretCode: "",
  };
  return (
    <Formik method="GET" initialValues={initialValues}>
      {({ isSubmitting }) => {
        return <p> results here</p>;
      }}
