import React, { useState, useEffect } from "react";
import { TextField } from "material-ui-formik-components/TextField";
import { Select } from "material-ui-formik-components/Select";
import { ErrorMessage, Formik, Form, Field } from "formik";
import {
  Grid,
  Typography,
  Paper,
  Button,
  LinearProgress,
  CircularProgress,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useMutation, useApolloClient, useQuery } from "@apollo/react-hooks";
import Notification from "../utils/Notification";

import * as Yup from "yup";
import useForm from "../utils/useForm";
import { createNewReportMutation } from "../queries&Mutations&Functions/Mutations";
import {
  getSelectedObject,
  removeTypename,
} from "../queries&Mutations&Functions/Functions";
import {
  getExamSessionQuery,
  getAllExamsQuery,
  getAllSessionsQuery,
  getSingleCenterExamSessionQuery,
  getSingleCenterQuery,
  getCESExaminerQuery,
} from "../queries&Mutations&Functions/Queries";

const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    // fontSize: 100,
  },
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "30vw",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  picStyled: {
    paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "30vw",
  },
});

const validationSchema = Yup.object().shape({
  reportName: Yup.string().required("Titre du rapport obligatoire"),
  examID: Yup.string().required("Choix De l'examen Obligatoire"),
  sessionID: Yup.string().required("Choix de la session Obligatoire"),

  examinerCode: Yup.string().required("Code secret examinateur Obligatoire"),
  centerNumber: Yup.number()
    .min(0, "Pas de note négative")
    .required("No du centre obligatoire"),
});

const CreateReport = () => {
  const classes = useStyles();
  const initialValues = {
    reportName: "",
    reportImage: "",
    centerNumber: "",
    examinerCode: "",
  };

  const [notify, setNotify] = useState({
    isOpen: false,
    message: "",
    type: "",
  });
  const [state, setState, handleSelectChange, handleInputChange] = useForm({
    reportImage: "",
    centerNumber: "",
    examID: "",
    sessionID: "",
    exams: [],
    sessions: [],
    centerByNumber: "",
    examSessions: "",
    centerExamSessions: "",
    examinerCode: "",
  });


  const {
    reportImage,
centerNumber,
examID,
sessionID,
exams,
sessions,
centerByNumber,
examSessions,
centerExamSessions,
examinerCode,
}=state


  const makeExaminerObject = (profCodeValue) => {
    const storedProf = {
      examinerCode: `${profCodeValue}`,
    };
    return storedProf;
  };

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    const val = type === "number" ? parseInt(value) : value;
    setState({ [name]: val });
  };

  const uploadFile = async (e) => {
    const files = e.target.files;
    const data = new FormData();
    data.append("file", files[0]);
    data.append("upload_preset", "ineximages");
    const res = await fetch(
      "https://api.cloudinary.com/v1_1/inex/reportImage/upload",
      {
        method: "POST",
        body: data,
      }
    );
    const file = await res.json();
    console.log(file);
    setState({ ...state, reportImage: file.secure_url });
  };

  const loadExamData = async () => {
    const { data } = await client.query({
      query: getAllExamsQuery,
    });

    const getExams = data && data.exams;
    console.log(getExams);
    const refinedExams = getExams && removeTypename(getExams);
    setState((prev) => ({
      ...prev,
      exams: refinedExams,
    }));
  };
  const removeExamName =
    exams && exams.map(({ examName, ...others }) => others);

  const getExamName = exams && {
    ...getSelectedObject(removeExamName, examID),
  };
  const getExamsOptions =
    exams && exams.map((item) => ({ value: item.id, label: item.examName }));

  const loadSessionData = async () => {
    const { data } = await client.query({
      query: getAllSessionsQuery,
    });

    const getSessions = data && data.sessions;
    const refinedSessions = getSessions && removeTypename(getSessions);
    setState((prev) => ({
      ...prev,
      sessions: refinedSessions,
    }));
  };
  console.dir(state);

  const getSessionName = sessions && {
    ...getSelectedObject(sessions, sessionID),
  };

  const getSessionsOptions =
    sessions &&
    sessions.map((item) => ({ value: item.id, label: item.sessionName }));

  useEffect(() => {
    loadExamData();
    loadSessionData();
  }, []);

  console.dir(getSessionName);

  const loadExamSessionData = async () => {
    console.dir("running exam session query");
    const { data, loading } = await client.query({
      skip: !examID || !sessionID,
      query: getExamSessionQuery,
      variables: {
        exam: examID && getExamName,
        session: sessionID && getSessionName,
      },
    });

    console.log(data);
    const getExamSessions = data && data.examSessions;
    const refinedES = getExamSessions && removeTypename(getExamSessions);
    const reducedES = refinedES && refinedES[0];
    console.log(reducedES);
    
    setState((prev) => ({
      ...prev,
      examSessions: reducedES,
    }));
  };
  console.dir(state);

  useEffect(() => {
    loadExamSessionData();
  }, [examID, sessionID]);

  const loadCenterData = async () => {
    const { data } = await client.query({
      skip: !centerNumber,
      query: getSingleCenterQuery,
      variables: { centerNumber: centerNumber },
    });

    const { centerByNumber } = { ...data };
    const newCenterByNumber = removeTypename(centerByNumber);
    console.log(newCenterByNumber);
    setState((state) => ({ ...state, centerByNumber: newCenterByNumber }));
  };

  useEffect(() => {
    loadCenterData();
  }, [centerNumber]);




  const loadCESData = async () => {
    const { error, data } = await client.query({
      query: getSingleCenterExamSessionQuery,
      variables: {
        examSession: examSessions && examSessions,
        center: centerByNumber && centerByNumber,
      },
    });

    console.log(data);
    const getCenterExamSessionsByCode = data && data.centerExamSessionsByCode;
    console.log(getCenterExamSessionsByCode);
    const refinedCenterExamSessions =
      getCenterExamSessionsByCode &&
      removeTypename(getCenterExamSessionsByCode);
    setState((prev) => ({
      ...prev,
      centerExamSessions: refinedCenterExamSessions,
    }));
  };

  useEffect(() => {
    loadCESData();
  }, [centerNumber, examSessions]);

  // transform the array into a single object
  const getCESID = centerExamSessions && centerExamSessions[0];
  console.log(getCESID);



  console.log(centerByNumber);

  // const getCenterExamSessionsByCode =
  //   dataCES && dataCES.centerExamSessionsByCode;
  // console.log(getCenterExamSessionsByCode);
  // // remove typename from the object
  // const refinedCenterExamSessionsByCode =
  //   getCenterExamSessionsByCode && removeTypename(getCenterExamSessionsByCode);
  // // transform the array into a single object
  // const getCES =
  //   refinedCenterExamSessionsByCode && refinedCenterExamSessionsByCode[0];
  // console.log(getCES);

  const {
    data: dataExaminer,
    loading: loadingExaminer,
    error: errExaminer,
  } = useQuery(getCESExaminerQuery, {
    skip: !getCESID || !state.examinerCode,
    variables: {
      centerExamSession: getCESID && getCESID,
      examiner: state.examinerCode && makeExaminerObject(state.examinerCode),
    },
  });

  const getCESExaminers =
    dataExaminer && dataExaminer.getCenterExamSessionExaminers;
  console.log(getCESExaminers);
  const refinedCESExaminer = getCESExaminers && removeTypename(getCESExaminers);
  const reducedCESExaminer = refinedCESExaminer && refinedCESExaminer[0];
  console.log(reducedCESExaminer);

  const [createReport, { loading, error }] = useMutation(
    createNewReportMutation
  );
  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={async (values, { setSubmitting, resetForm }) => {
        const res = await createReport({
          variables: {
            ...values,
            reportImage: state.reportImage,
            centerExamSessionExaminer: reducedCESExaminer && reducedCESExaminer,
          },
        });
        setTimeout(() => {
          console.log(JSON.stringify(values, null, 2));
          console.log(res);
          setNotify({
            isOpen: true,
            message: "Rapport créé avec success",
            type: "success",
          });
          resetForm({ values: "" });
          setSubmitting(false);
        }, 200);
      }}
    >
      {({ submitForm, isSubmitting }) => {
        return (
          <div className={classes.centerAll}>
            <Paper className={classes.pageStyled} elevation={3}>
              <Form aria-busy={isSubmitting}>
                {(isSubmitting || loading) && <LinearProgress />}
                <Grid className={classes.centerAll} container>
                  <Grid container className={classes.centerAll}>
                    <Grid item>
                      <Error error={error} />
                      <Typography
                        className={classes.titleStyled}
                        color="primary"
                        gutterBottom
                        variant="h5"
                        component="h6"
                      >
                        Nouvel Arrondissement
                      </Typography>
                    </Grid>
                  </Grid>
                  <Grid container className={classes.centerAll}>
                    <Grid item className={classes.centerAll}>
                      <Field
                        variant="outlined"
                        helpertext={<ErrorMessage name="centerName" />}
                        component={TextField}
                        name="centerName"
                        type="text"
                        label=" Libellé du centre"
                         value={
                          (centerByNumber && centerByNumber.centerCode) || ""
                        }
                        disabled
                      />
                      <Field
                        variant="outlined"
                        helpertext={<ErrorMessage name="centerNumber" />}
                        component={TextField}
                        value={state.centerNumber}
                        onChange={handleChange}
                        name="centerNumber"
                        type="number"
                        label="Numero du centre"
                        disabled={isSubmitting}
                      />
                      <Field
                        component={TextField}
                        variant="outlined"
                        helpertext={<ErrorMessage name="file" />}
                        label="Choisir ta photo"
                        name="file"
                        type="file"
                        onChange={uploadFile}
                        disabled={isSubmitting}
                      />

                      <Field
                        variant="outlined"
                        helpertext={<ErrorMessage name="sessionID" />}
                        component={Select}
                        options={getSessionsOptions}
                        onChange={(event) => {
                          handleSelectChange(event, "sessionID");
                        }}
                        name="sessionID"
                        placeholder="La session"
                        disabled={isSubmitting}
                      />
                      <Field
                        variant="outlined"
                        helpertext={<ErrorMessage name="examID" />}
                        component={Select}
                        options={getExamOsptions}
                        onChange={(event) => {
                          handleSelectChange(event, "examID");
                        }}
                        name="examID"
                        placeholder="L'Examen"
                        disabled={isSubmitting}
                      />

                      <Field
                        variant="outlined"
                        helpertext={<ErrorMessage name="reportName" />}
                        component={TextField}
                        name="reportName"
                        type="text"
                        label="Titre du Rapport"
                        disabled={isSubmitting}
                      />
                      <Field
                        variant="outlined"
                        helpertext={<ErrorMessage name="examinerCode" />}
                        component={TextField}
                        onChange={handleChange}
                        value={state.examinerCode}
                        name="examinerCode"
                        type="text"
                        label="Code Secret de l'examinateur"
                        disabled={isSubmitting}
                      />

                      <div className={classes.picStyled}>
                        {!state.reportImage ? (
                          <CircularProgress />
                        ) : (
                          <img
                            style={{
                              height: "15rem",
                              width: "15rem",
                              display: "grid",
                              backgroundSize: "contain",
                              backgroundPosition: "center",
                              placeItems: "center",
                              borderRadius: "0.5rem",
                            }}
                            src={state.reportImage}
                            alt="Upload image"
                          />
                        )}
                      </div>
                      <Notification notify={notify} setNotify={setNotify} />

                      <Button disabled={isSubmitting} onClick={submitForm}>
                        {(isSubmitting || loading) && <CircularProgress />}
                        Valid
                        {isSubmitting
                          ? "Nouveau rapport en création"
                          : "Créer Rapport"}
                      </Button>
                    </Grid>
                  </Grid>
                </Grid>
              </Form>
            </Paper>
          </div>
        );
      }}
    </Formik>
  );
};
export default CreateReport;
