import React, { useEffect } from "react";
import Link from "next/link";
import useForm from "../utils/useForm";
import { StyledButton, ButtonStyled, StyledForm } from "../utils/FormInputs";
import { format } from "date-fns";
import { ErrorMessage, Formik, Form, Field } from "formik";
import {
  Grid,
  Typography,
  Paper,
  Button,
  LinearProgress,
  CircularProgress,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useMutation, useApolloClient } from "@apollo/react-hooks";
import { MuiPickersUtilsProvider, DatePicker } from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";
import { singleCandidateQuery } from "../queries&Mutations&Functions/Queries";

const useStyles = makeStyles({
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "50%",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },
  MuiFormControl: {
    root: {
      margin: "0rem",
    },
  },

  partTwo: {
    display: "grid",
    gridTemplateRows: "1fr 1fr ",
  },

  picStyled: {
    paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "40%",
  },
});

const SingleCandidateHooks = ({ id }) => {
  const client = useApolloClient();
  const classes = useStyles();
  const router = useRouter();
  const [state, setState] = useForm({});

  const loadCandData = async () => {
    const { error, data } = await client.query({
      query: singleCandidateQuery,
      variables: { id: id },
    });
    console.log(data);
    const { candidate } = data;
    setState({ candidate });
  };
  useEffect(() => {
    loadCandData();
  }, []);

  console.log(state.candidate);

  const {
    momName,
    dadName,
    candCode,
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    gender,
    phoneNumb,
    placeOfBirth,
    dateOfBirth,
    birthCertNumber,
  } = { ...state.candidate };
  const initialValues = {
    momName: "",
    dadName: "",
    candCode: "",
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    phoneNumb: "",
    gender: "",
    placeOfBirth: "",
    dateOfBirth: "",
    birthCertNumber: "",
  };
  return (
    <Formik initialValues={initialValues}>
      {({ values, isSubmitting }) => (
        <div className={classes.centerAll}>
          <Paper className={classes.pageStyled} elevation={13}>
            <Form aria-busy={isSubmitting}>
              {(isSubmitting || loadingMut) && <LinearProgress />}
              <Grid className={classes.centerAll} container>
                <Grid container className={classes.centerAll}>
                  <Grid item>
                    <Error error={error} />
                    <Typography
                      className={classes.titleStyled}
                      color="primary"
                      gutterBottom
                      variant="h5"
                      component="h6"
                    >
                      Recu Candidat
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={6}>
                  <Grid item className={classes.centerAll} xs={12} sm={6}>
                    <p>
                      <strong>Nom:</strong> <span>{cand1stName}</span>
                    </p>
                    <p>
                      <strong>Prenoms: </strong>
                      <span> {cand2ndName}</span>
                    </p>

                    <p>
                      <strong>Autres Noms:</strong>
                      <span> {cand3rdName}</span>
                    </p>

                    <strong>Naissance:</strong>
                    <hr />
                    <p>
                      <strong>Sexe:</strong> <span>{gender}</span>
                    </p>
                    <p>
                      <strong>Date : </strong>
                      <span>{format(dateOfBirth, " DD, MMM, YYYY ")}</span>
                    </p>
                    <p>
                      <strong>Lieu:</strong> <span>{placeOfBirth}</span>
                    </p>

                    <strong>Nom des parents:</strong>
                    <hr />

                    <p>
                      <strong>Père:</strong>
                      <span> {dadName}</span>
                    </p>
                    <p>
                      <strong>Mère:</strong>
                      <span> {momName}</span>
                    </p>

                    <p>
                      <strong>Acte Naissance:</strong>
                    </p>
                    <hr />

                    <p>
                      <strong>No:</strong>
                      <span> {birthCertNumber}</span>
                    </p>
                    <p>
                      <strong>Tel: </strong> <span>{phoneNumb}</span>
                    </p>
                  </Grid>
                  <Grid item className={classes.centerAll} xs={12} sm={6}>
                    <p>
                      <strong>Email:</strong>
                      <span> {email}</span>
                    </p>

                    <p>
                      <strong>Code:</strong> <span> {candCode}</span>
                    </p>
                    <CandImg>
                      <div>{<img src={image} alt={cand1stName} />}</div>
                    </CandImg>
                  </Grid>
                </Grid>
                <Grid item className={classes.centerAll} xs={12} sm={6}>
                  <Button>
                    <Link
                      href={{
                        pathname: "../updates/updateCand",
                        query: { id },
                      }}
