import React, { useEffect } from "react";
import Error from "../ErrorMessage.js";
import * as Yup from "yup";
import FRadioGroup from "../muiComponents/controls/SygefexMuiRadioGroup";
import { useRouter } from "next/router";
import { format } from "date-fns";

import { ErrorMessage, Formik, Form, Field } from "formik";
import { Grid, Typography, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient } from "@apollo/react-hooks";
import { getCandidateRegistrationInfoQuery } from "../queries&Mutations&Functions/Queries";

import { removeTypename } from "../queries&Mutations&Functions/Functions";
import useForm from "../utils/useForm";

const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",

    // fontSize: 100,
  },

  picStyled: {
    // paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
    borderRadius: "0.5rem",
  },

  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    padding: "1rem",
    maxWidth: "90%",
    // backgroundColor: "#c89666",
  },
  generalContainer: {
    display: "grid",
    gridTemplateRows: "2fr 1fr",
    padding: "1rem",
    // backgroundColor: "#c89666",
  },
  examInfo: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    padding: "1rem",
    backgroundColor: "#c89666",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    // minWidth: "30vw",
    // minWidth: "40%",
    // maxWidth: "100vw",
  },
});

const NewRegistrationReceipt = ({ id }) => {
  const classes = useStyles();
  const router = useRouter();
  const client = useApolloClient();

  const [state, setState] = useForm({});

  const loadRegistrationData = async () => {
    const { error, data } = await client.query({
      query: getCandidateRegistrationInfoQuery,
      variables: { id },
    });

    console.log(data);
    const { registration } = data;
    setState({ registration });
    return data;
  };

  useEffect(() => {
    loadRegistrationData();
  }, [id]);

  console.log(state.registration);
  const {
    aptitude,

    candidate,
    centerExamSessionSpecialty,
    createdAt,
    candRegistrationNumber,
  } = { ...state.registration };

  const { centerExamSession, specialty } = { ...centerExamSessionSpecialty };

  const { examSession, center } = { ...centerExamSession };
  const { exam, session } = { ...examSession };
  const { centerName } = { ...center };
  const { examName } = { ...exam };
  const { sessionName } = { ...session };
  const { specialtyName, subjectSpecialty } = { ...specialty };

  const {
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    gender,
    placeOfBirth,
    dateOfBirth,
    EPF1,
    EPF2,
  } = { ...candidate };

  const initialValues = {
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    EPF1: "",
    EPF2: "",
    placeOfBirth: "",
    dateOfBirth: "",
  };

  return (
    <Formik initialValues={initialValues}>
      {({ submitForm, setFieldValue, isSubmitting }) => {
        return (
          <div className={classes.centerAll}>
            <Paper className={classes.pageStyled} elevation={13}>
              <Form aria-busy={isSubmitting}>
                {isSubmitting && <LinearProgress />}
                <Grid className={classes.centerAll} container>
                  <Grid container className={classes.centerAll}>
                    <Grid item>
                      <Typography
                        className={classes.titleStyled}
                        color="primary"
                        gutterBottom
                        variant="h5"
                        component="h6"
                      >
                        Recu d'Inscription aux examens
                      </Typography>
                    </Grid>
                  </Grid>
                  <Grid spacing={10} container className={classes.examInfo}>
                    <Grid xs={12} sm={6} container>
                      <Grid item xs={12}>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Centre D'Examen: </strong>
                            {centerName}
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Examen: </strong> {examName}
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Session: </strong> {sessionName}
                          </p>
                        </Typography>
                      </Grid>
                      <Grid item xs={12}>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong>Info Inscritption: </strong>
                            <hr />
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Numero: </strong> {candRegistrationNumber}
                          </p>
                        </Typography>
                       
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> Aptitude: </strong> {aptitude}
                          </p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <p>
                            <strong> SERIE: </strong>
                            {specialtyName}
                          </p>
                        </Typography>
                         <Typography color="primary" variant="body1">
                        <p>
                          <strong>Date : </strong>
                         {format(dateOfBirth, " DD, MMM, YYYY ")}</p>
                      </Typography>
                      </Grid>
                    </Grid>
                    <Grid xs={12} sm={6} item>
                      {
                        <img
                          className={classes.picStyled}
                          src={image}
                          alt={cand1stName}
                        />
                      }
                    </Grid>
                  </Grid>
                  <Grid
                    spacing={10}
                    container
                    className={classes.generalContainer}
                  >
                    <Grid xs={12} sm={6} item>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Nom:</strong>{cand1stName}</p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Prenoms: </strong>
                          {cand2ndName}</p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Autres Noms:</strong>
                         {cand3rdName}</p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <strong>Naissance:</strong>
                        <hr />
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Sexe:</strong> {gender}</p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Date : </strong>
                         {format(dateOfBirth, " DD, MMM, YYYY ")}</p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Lieu:</strong> {placeOfBirth}</p>
                      </Typography>
                    </Grid>

                    <Grid xs={12} sm={6} item>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Tel: </strong> {email}
                          </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Email:</strong>
                           {email}</p>
                      </Typography>

                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>EPF1:</strong> <p> {EPF1}</p>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>EPF2:</strong> <p> {EPF2}</p>
                        </p>
                      </Typography>
                    </Grid>
                  </Grid>
                </Grid>
              </Form>
            </Paper>
          </div>
        );
      }}
    </Formik>
  );
};
export default NewRegistrationReceipt;
