import Sidebar from "./Sidebar";

const drawerWidth = 370;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    // [theme.breakpoints.up("sm")]: {
    //   width: `calc(100% - ${drawerWidth}px)`,
    //   marginLeft: drawerWidth,
    // },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },

  userName: {
    alignItems: "center",
  },
}));

const items = [
  "divider",
  {
    url: "../../creates/login",
    name: "home",
    label: "Home",
    Icon: HomeIcon,
  },
  "divider",
  {
    // url: "../../creates/newRegion",
    name: "centreManagement",
    label: "Centre d'examen",
    Icon: ReceiptIcon,
    items: [
      {
        // url: "../../creates/newRegion",
        name: "examStages",
        label: "Phases",
        items: [
          {
            // url: "../../show/results/candCodeResults",
            name: "centerExamPreps",
            label: "Preparation",
            items: [
              {
                url: "../../creates/newCES",
                name: "registerCenterForExams",
                label: "Inscrire centre aux examens",
              },
              {
                url: "../../creates/newCESS",
                name: "Specialties",
                label: "Series offertes par centre",
              },
              {
                url: "../../creates/newGroupCand",
                name: "candidateInfo",
                label: "Renseignement Candidats",
              },
            ],
          },
          {
            // url: "../../show/results/candCodeResults",
            name: "centerExamRegistration",
            label: "Inscription",
            items: [
              {
                url: "../../creates/newRegister",
                name: "registerCandidates",
                label: "Inscrire Candidats aux examens",
              },
              {
