const LayoutDrawer = (props) => {
  const { window, children } = props;
  const classes = useStyles();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    // <div style={{ backgroundColor: "#004666", color: "#000" }}>
    // <div style={{ backgroundColor: "#044562", color: "#000" }}>
    // <div style={{ backgroundColor: "#0780b7", color: "#000" }}>
    <div style={{ backgroundColor: "#abb", color: "#000" }}>
      <div className={classes.toolbar} />
      <Divider />
      <Sidebar items={items} />
    </div>
  );
  const container =
    window !== undefined ? () => window().document.body : undefined;
  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        // style={{ backgroundColor: "#4a8ba8" }}
        style={{ backgroundColor: "#fff" }}
        position="fixed"
        className={classes.appBar}
      >
        <Toolbar>
          <Grid container>
            <Grid item xs={6} sm={10}>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                onClick={handleDrawerToggle}
                className={classes.menuButton}
              >
                <MenuIcon />
              </IconButton>

              <Typography variant="h6" component="h5" noWrap>
                Sygefex-OBC
              </Typography>
            </Grid>
            <Grid className={classes.userName} item xs={6} sm={2}>
              <User>
                {({ data: { me } }) => (
                  <div>
                    {me ? (
                      <div
                        style={{
                          display: "grid",
                          gridTemplateColumns: "1fr 1fr",
                          gridGap: "1rem",
                        }}
                      >
                        <Typography variant="h6" component="h5" noWrap>
                          {me.name}
                        </Typography>
                        <Signout />
                      </div>
                    ) : (
                      <Link href="../../creates/login" passHref>
                        <Button
                          variant="outlined"
                          component="a"
                          style={{
                            color: "#4a8ba8",
                            paddingLeft: "0.3rem",
                            paddingRight: "0.3rem",
                            textTransform: "none",
                            backgroundColor: "#fff",
                          }}
                        >
                          Login
                        </Button>
                      </Link>
                    )}
                  </div>
                )}
              </User>
            </Grid>
          </Grid>
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of urls. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <main className={classes.content}>{children}</main>
    </div>
  );
};

