import React, { useState, useEffect } from "react";
import { centerExamSessionForResultsQuery } from "../../queries&Mutations&Functions/Queries";
import RegistrationDetails from "./RegistrationDetails";
import Error from "../../ErrorMessage";
import tableIcons from "../utils/icons/tableIcons";
import ConfirmDialog from "../utils/ConfirmDialog";
import AddPopup from "../utils/AddPopup";
import UpdatePopup from "../utils/UpdatePopup";
import { getAllRegionsDivisionsTownsCentersQuery } from "../queries&Mutations&Functions/Queries";
import MaterialTable, { MTableToolbar } from "material-table";

import {
  Edit as EditIcon,
  Add as AddIcon,
  Delete as DeleteIcon,
} from "@material-ui/icons";
import { Formik, Form } from "formik";
import { Grid, Typography, Paper, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient } from "@apollo/react-hooks";

const useStyles = makeStyles({
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "50%",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  partTwo: {
    display: "grid",
    gridTemplateRows: "1fr 1fr ",
  },

  picStyled: {
    // paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
    borderRadius: "0.5rem",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "50%",
  },
});

const CenterRegistrationDetails = ({ id }) => {
  // static propTypes = { id: PropTypes.string.isRequired };
  const client = useApolloClient();
  const classes = useStyles();
  const [state, setState] = useState({
    columns: [
      {
        field: "No",
        title: "Region",
        options: {
          width: 30,
          headerNoWrap: true,
        },
      },

      {
        field: "divName",
        title: "Dept",
        options: {
          width: 30,
          headerNoWrap: true,
        },
      },
      {
        field: "townName",
        title: "Ville",
        options: {
          width: 30,
          headerNoWrap: true,
        },
      },
      {
        field: "centerName",
        title: "Centre",
        options: {
          width: 400,
          headerNoWrap: true,
        },
      },

      {
        field: "centerNumber",
        title: "No",
        // grouping: false,
        type: "numeric",
        options: {
          width: 30,
          headerNoWrap: true,
        },
      },
    ],
    center: "",
    exam: "",
    session: "",
    registration: [],
  });

  const initialValues = {};

  const loadCenterRegistrationInfo = async () => {
    const { data } = await client.query({
      query: centerExamSessionForResultsQuery,
      variables: { id },
    });

    const getCenterExamSession = data.centerExamSession;
    const { registration, center, examSession } = getCenterExamSession;
    const { exam, session } = { ...examSession };

    setState((prev) => ({ ...prev, center, registration, exam, session }));
  };

  useEffect(() => {
    loadCenterRegistrationInfo();
  }, []);
  const { center, session, exam, registration } = state;

  return (
    <Formik initialValues={initialValues}>
      {({ values, isSubmitting }) => (
        <div className={classes.centerAll}>
          <Paper className={classes.pageStyled} elevation={13}>
            <Form aria-busy={isSubmitting}>
              <Grid className={classes.centerAll} container>
                <Grid container className={classes.centerAll}>
                  <Grid item>
                    <Typography
                      className={classes.titleStyled}
                      color="primary"
                      gutterBottom
                      variant="h5"
                      component="h6"
                    >
                      Resultats Complets Du: {exam && exam.examName}, Session:{" "}
                      {session && session.sessionName}, centre:{" "}
                      {center && center.centerCode}
                    </Typography>
                  </Grid>
                </Grid>
                <div>
                  <MaterialTable
                    className={classes.head}
                    components={{
                      Toolbar: (props) => (
                        <div
                          style={{
                            backgroundColor: "#829079",
                            borderTopRightRadius: "0.5rem",
                            borderTopLeftRadius: "0.5rem",
                            color: "#ede6b9",
                          }}
                        >
                          <MTableToolbar
                            style={{
                              // textColor: "#000",
                              textColor: "#fff",
                            }}
                            {...props}
                          />
                        </div>
                      ),
                    }}
                    icons={tableIcons}
                    title="Liste des Candidats..................                            "
                    columns={state.columns}
                    data={state.registration}
                    options={{
                      actionsColumnIndex: -1,
                      grouping: true,
                      paging: true,
                      pageSize: 50, // make initial page size
                      emptyRowsWhenPaging: false, //to make page size fix in case of less data rows
                      pageSizeOptions: [25, 50, 75, 100, 150], // rows selection options
                      headerStyle: {
                        paddingTop: "0.5rem",
                        paddingBottom: "0.5rem",
                        // backgroundColor: "#abb",
                        color: "#ede6b9",
                        backgroundColor: "#b9925e",
                        maxHeight: "0.5rem !important",
                      },
                      rowStyle: {
                        color: "#000",
                      },
                    }}
                    actions={actions}
                  />
                  <AddPopup
                    title="Nouveau Centre"
                    isOpen={isAddPopupOpen}
                    onClose={handleAddPopupClose}
                  >
                    <NewCenter />
                  </AddPopup>
                  <ConfirmDialog
                    title={deleteConfirmDialog.title}
                    subtitle={deleteConfirmDialog.subtitle}
                    isOpen={deleteConfirmDialog.isOpen}
                    onClose={handleDeleteConfirmDialog}
                    onConfirm={deleteConfirmDialog.onConfirm}
                  />
                  <Notification notify={notify} setNotify={setNotify} />

                  <UpdatePopup
                    // title={updatePopupState.id}
                    isOpen={updatePopupState.isOpen}
                    onClose={handleUpdatePopupClose}
                  >
                    <UpdateCenter id={updatePopupState.id} />
                  </UpdatePopup>
                </div>
              </Grid>
            </Form>
          </Paper>
        </div>
      )}
    </Formik>
  );
};

export default CenterRegistrationDetails;

