import React, { useEffect } from "react";
import { useRouter } from "next/router";
import { format } from "date-fns";

import { Formik, Form } from "formik";
import { Grid, Typography, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient } from "@apollo/react-hooks";
import { getCandidateRegistrationInfoQuery } from "../queries&Mutations&Functions/Queries";

import useForm from "../utils/useForm";

const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",

    // fontSize: 100,
  },

  picStyled: {
    height: "14rem",
    width: "12rem",
    marginTop: "2rem",
    display: "grid",
    placeItems: "center",
    borderRadius: "0.5rem",
  },

  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    padding: "1rem",
    maxWidth: "80%",
    // backgroundColor: "#c89666",
  },
  generalContainer: {
    display: "flex",
    flexDirection: "row",
    marginTop: "1rem",
  },
  examInfo: {
    display: "grid",
    gridTemplateColumns: "2fr 1fr",
    padding: "1rem",
    backgroundColor: "#c89666",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    // minWidth: "30vw",
    // minWidth: "40%",
    // maxWidth: "100vw",
  },
});

const NewRegistrationReceipt = ({ id }) => {
  const classes = useStyles();
  const router = useRouter();
  const client = useApolloClient();

  const [state, setState] = useForm({});

  const loadRegistrationData = async () => {
    const { error, data } = await client.query({
      query: getCandidateRegistrationInfoQuery,
      variables: { id },
    });

    console.log(data);
    const { registration } = data;
    setState({ registration });
    return data;
  };

  useEffect(() => {
    loadRegistrationData();
  }, [id]);

  console.log(state.registration);
  const {
    aptitude,
    candidate,
    centerExamSessionSpecialty,
    createdAt,
    candRegistrationNumber,
  } = { ...state.registration };

  const { centerExamSession, specialty } = { ...centerExamSessionSpecialty };

  const { examSession, center } = { ...centerExamSession };
  const { exam, session } = { ...examSession };
  const { centerName } = { ...center };
  const { examName } = { ...exam };
  const { sessionName } = { ...session };
  const { specialtyName, specialtyCode } = { ...specialty };

  const {
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    phoneNumb,
    gender,
    placeOfBirth,
    dateOfBirth,
    EPF1,
    EPF2,
  } = { ...candidate };

  const initialValues = {
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    EPF1: "",
    EPF2: "",
    placeOfBirth: "",
    dateOfBirth: "",
  };

  return (
    <Formik initialValues={initialValues}>
      {({ submitForm, setFieldValue, isSubmitting }) => {
        return (
          <div className={classes.centerAll}>
            <Paper className={classes.pageStyled} elevation={13}>
              <Form aria-busy={isSubmitting}>
                {isSubmitting && <LinearProgress />}
                <Grid className={classes.centerAll} container>
                  <Grid container className={classes.centerAll}>
                    <Grid item>
                      <Typography
                        className={classes.titleStyled}
                        gutterBottom
                        variant="h5"
                        component="h6"
                      >
                        Recu d'Inscription aux examens
                      </Typography>
                    </Grid>
                  </Grid>
                  <Grid spacing={10} container className={classes.examInfo}>
                    <Grid container xs={12}>
                      <Grid item xs={12}>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong> Centre D'Examen: </strong>
                          <p> {centerName}</p>
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong> Examen: </strong> {examName}
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong> Session: </strong>
                          {sessionName}
                        </Typography>
                      </Grid>
                      <Grid item xs={12}>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong>Info Inscritption: </strong>
                          <hr />
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong> Numero: </strong> {candRegistrationNumber}
                        </Typography>

                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong> Aptitude: </strong> {aptitude}
                        </Typography>
                        <Typography
                          // style={{ color: "#fff" }}
                          variant="body1"
                          component="h5"
                          noWrap
                        >
                          <strong> SERIE: </strong>
                          {specialtyCode} - {specialtyName}
                        </Typography>
                        <Typography variant="body1">
                          <strong>Date : </strong>
                          {format(createdAt, " DD, MMM, YYYY ")}
                        </Typography>
                      </Grid>
                    </Grid>
                    <Grid xs={12} sm={6} item>
                      {
                        <img
                          className={classes.picStyled}
                          src={image}
                          alt={cand1stName}
                        />
                      }
                    </Grid>
                  </Grid>
                  <Grid
                    spacing={10}
                    container
                    className={classes.generalContainer}
                  >
                    <Grid xs={12} sm={6} item>
                      <Typography variant="body1">
                        <strong>Nom:</strong>
                        {cand1stName}
                      </Typography>
                      <Typography variant="body1">
                        <strong>Prenoms: </strong>
                        {cand2ndName}
                      </Typography>
                      <Typography variant="body1">
                        <strong>Autres Noms:</strong>
                        {cand3rdName}
                      </Typography>
                      <Typography variant="body1">
                        <strong>Email: </strong> {email}
                      </Typography>
                      <Typography variant="body1">
                        <strong>Lieu:</strong> {placeOfBirth}
                      </Typography>
                    </Grid>

                    <Grid xs={12} sm={6} item>
                      <Typography variant="body1">
                        <strong>Date : </strong>
                        {format(dateOfBirth, " DD, MMM, YYYY ")}
                      </Typography>

                      <Typography variant="body1">
                        <strong>Tel: </strong> {phoneNumb}
                      </Typography>

                      <Typography variant="body1">
                        <strong>Sexe:</strong> {gender}
                      </Typography>

                      <Typography variant="body1">
                        <strong>EPF1:</strong>
                        {EPF1}
                      </Typography>
                      <Typography variant="body1">
                        <strong>EPF2:</strong>
                        {EPF2}
                      </Typography>
                    </Grid>
                  </Grid>
