import React, { Component } from 'react';
import Form from '../../styles/Form';
import { MinimStyledPage } from '../../styles/StyledPage';
import Error from '../../ErrorMessage';
import styled from 'styled-components';
import Router from 'next/router'
import Downshift from 'downshift'
import debounce from 'lodash.debounce'
import { ApolloConsumer } from 'react-apollo'
import { getCandidateIDQuery } from '../../queries&Mutations&Functions/Queries';

const StyledDivision = styled.div`
	display: grid;
	grid-template-columns: 1fr;
	text-align: center;
	margin: 0 auto;
`;

const CenterSelect = styled.div`
	display: block;
	text-align: center;
	margin: 0 auto;
`;

class CandidateDetailedResults extends Component {
    state = {
        id: "",
        loading: "false"
    };

    handleChange = (e) => {
        this.setState({ loading: true })
        const { name, value, type } = e.target;
        const val = type === 'number'
            ? parseInt(value)
            : value;
        this.setState({ [name]: val });
    };

    resetForm = () => {
        this.setState({ candCode: '' });
    };

    makeCandVariableObject = (candCodeValue) => {
        const storedCandidate = {
            candCode: `${candCodeValue}`
        };
        return storedCandidate;
    };
    onChange = debounce(async (e, client) => {
        console.log('seaching')
        const res = await client.query({
            query: getCandidateIDQuery,
            variables: { candCode: e.target.value }
        })
        this.setState({
            id: res.data.candidateCode.id,
            loading: false,
        })
    }, 400)
    render() {
        const { id } = this.state;

        id && Router.push({
            pathname: '/show/results/allCandResults',
            query: { id },

        });
        return (
            <MinimStyledPage >
                <Form
                    onSubmit={async (e) => {
                        e.preventDefault();
                        this.resetForm();
                    }}>
                    <h4 >
                        Vérifier vos résultats
                   </h4>
                    <fieldset >
                        <StyledDivision >
                            <CenterSelect>
                                <ApolloConsumer>
                                    {(client) => (

                                        <input
                                            type="search"
                                            placeholder="Code Candidat"
                                            onChange={(e) => {
                                                e.persist()
                                                this.onChange(e, client)
                                            }}
                                        />
                                    )}
                                </ApolloConsumer>
                            </CenterSelect>
                        </StyledDivision>
                    </fieldset>
                </Form>
            </MinimStyledPage>

        );
    }
}

export default CandidateDetailedResults;

