import React, { useEffect, useState } from "react";
import MaterialTable, { MTableToolbar } from "material-table";
import { Paper } from "@material-ui/core";
import { useApolloClient } from "@apollo/react-hooks";
import { getAllRegionsAndDivisionsQuery } from "../queries&Mutations&Functions/Queries";
import tableIcons from "../utils/icons/tableIcons";
import Popup from "../utils/Popup";
import Button from "@material-ui/core/Button";
import NewDivision from "./NewDivision";

const DivisionList = () => {
  const client = useApolloClient();

  const { openPopup, setOpenPopup } = useState(false);
  const [state, setState] = useState({
    columns: [
      {
        field: "regName",
        title: "Region Name",
      },
      {
        field: "regCode",
        title: "Region Code",
        grouping: false,
      },
      { field: "divName", title: "Division Name" },
      { field: "divCode", title: "Division Code", grouping: false },
    ],
    data: [],
  });

  const loadDivisionData = async () => {
    const { error, data } = await client.query({
      query: getAllRegionsAndDivisionsQuery,
    });
    const { regions } = { ...data };

    const getDivisions = regions.map((item) => {
      const divInfos = item.division.map((division) => ({
        regName: item.regName,
        regCode: item.regCode,
        ...division,
      }));
      return divInfos;
    });

    setState((prev) => ({ ...prev, data: getDivisions.flat(1) }));
  };

  useEffect(() => {
    loadDivisionData();

    console.log(state.data);
  }, []);

  console.dir(state.data);

  const actions = [
    {
      icon: () => <AddIcon />,
      tooltip: "Add User",
      isFreeAction: true,
      onClick: (event, rowData) => {
        setOpenPopup(true);
      },
    },
    {
      icon: () => <EditIcon />,
      tooltip: "Edit User",
      onClick: (event, rowData) => {
        setOpenPopup(true);
      },
    },
  ];
  return (
    <Popup open={openPopup} setOpenPopup={setOpenPopup}>
      <Paper style={{ marginTop: "2rem" }}>
        <MaterialTable
          stickyHeader
          style={{ position: "sticky", top: 0 }}
          icons={tableIcons}
          title="Users List"
          columns={state.columns}
          data={state.data}
          actions={actions}
        />
      </Paper>
    </Popup>
  );
};
export default DivisionList;
