import { useRouter } from "next/router";
import React, { useEffect } from "react";
import Link from "next/link";
import useForm from "../utils/useForm";
import { Formik, Form } from "formik";
import { Grid, Typography, Paper, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient } from "@apollo/react-hooks";
import { singleExaminerQuery } from "../queries&Mutations&Functions/Queries";

const useStyles = makeStyles({
  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    marginTop: "2rem",
    padding: "1rem",
    minWidth: "50%",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  partTwo: {
    display: "grid",
    gridTemplateRows: "1fr 1fr ",
  },

  picStyled: {
    // paddingLeft: "1rem",
    height: "15rem",
    width: "12rem",
    display: "grid",
    placeItems: "center",
    borderRadius: "0.5rem",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    minWidth: "50%",
  },
});

const SingleExaminerMui = ({ id }) => {
  const client = useApolloClient();
  const classes = useStyles();
  const router = useRouter();
  const [state, setState] = useForm({});

  const loadExaminerData = async () => {
    const { error, data } = await client.query({
      query: singleExaminerQuery,
      variables: { id: id },
    });
    console.log(data);
    const { examiner } = data;
    setState({ examiner });
  };
  useEffect(() => {
    loadExaminerData();
  }, []);

  console.log(state.examiner);

  const {
    examinerCode,
    examiner1stName,
    examiner2ndName,
    examinerOtherNames,
    examinerEmail,
    gender,
    examinerPhone,
    examinerImage,
    examinerCNI,
  } = { ...state.examiner };

  const initialValues = {
    examinerCode: "",
    examiner1stName: "",
    examiner2ndName: "",
    examinerOtherNames: "",
    examinerEmail: "",
    examinerPhone: "",
    gender: "",
    examinerImage: "",
    examinerCNI: "",
  };
  return (
    <Formik initialValues={initialValues}>
      {({ values, isSubmitting }) => (
        <div className={classes.centerAll}>
          <Paper className={classes.pageStyled} elevation={13}>
            <Form aria-busy={isSubmitting}>
              <Grid className={classes.centerAll} container>
                <Grid container className={classes.centerAll}>
                  <Grid item>
                    <Typography
                      className={classes.titleStyled}
                      color="primary"
                      gutterBottom
                      variant="h5"
                      component="h6"
                    >
                      Recu Examinateur
                    </Typography>
                  </Grid>
                </Grid>
                <Grid container spacing={8}>
                  <Grid item xs={12} sm={6}>
                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Nom:</strong> <span>{examiner1stName}</span>
                      </p>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Prenoms: </strong>
                        <span> {examiner2ndName}</span>
                      </p>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Autres Noms:</strong>
                        <span> {examinerOtherNames}</span>
                      </p>
                    </Typography>

                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Sexe:</strong> <span>{gender}</span>
                      </p>
                    </Typography>

                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Tel:</strong>
                        <span> {examinerCNI}</span>
                      </p>
                    </Typography>
                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Tel: </strong> <span>{examinerPhone}</span>
                      </p>
                    </Typography>

                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Email:</strong>
                        <span> {examinerEmail}</span>
                      </p>
                    </Typography>

                    <Typography color="primary" variant="body1">
                      <p>
                        <strong>Code:</strong> <span> {examinerCode}</span>
                      </p>
                    </Typography>
                  </Grid>
                  <Grid item xs={12} sm={6} className={classes.picStyled}>
                    {
                      <img
                        className={classes.picStyled}
                        src={examinerImage}
                        alt={examiner1stName}
                      />
                    }
                    <Button>
                      <Link
                        href={{
                          pathname: "../updates/updateExaminer",
                          query: { id },
                        }}
                      >
                        <a target="_blank">Modifier </a>
                      </Link>
                    </Button>
                  </Grid>
                </Grid>
              </Grid>
            </Form>
          </Paper>
        </div>
      )}
    </Formik>
  );
};

export default SingleExaminerMui;

