import React, { useEffect } from "react";
import Error from "../ErrorMessage.js";
import * as Yup from "yup";
import FRadioGroup from "../muiComponents/controls/SygefexMuiRadioGroup";
import { useRouter } from "next/router";
import { format } from "date-fns";

import { ErrorMessage, Formik, Form, Field } from "formik";
import {
  Grid,
  Typography,
  Paper,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useApolloClient } from "@apollo/react-hooks";
import { getCandidateRegistrationInfoQuery } from "../queries&Mutations&Functions/Queries";

import {
  removeTypename,
} from "../queries&Mutations&Functions/Functions";
import useForm from "../utils/useForm";

const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",

    // fontSize: 100,
  },

  divStyled: {
    display: "grid",
    placeItems: "center",
    top: "2rem",
    height: "90vh",
  },
  pageStyled: {
    display: "grid",
    placeItems: "center",
    padding: "1rem",
    maxWidth: "80%",
    // backgroundColor: "#c89666",
  },
  generalContainer: {
    display: "grid",
    gridTemplateRows: "2fr 1fr",
    padding: "1rem",
    // backgroundColor: "#c89666",
  },

  titleStyled: {
    display: "grid",
    placeItems: "center",
  },

  centerAll: {
    display: "grid",
    placeItems: "center",
    // minWidth: "30vw",
    minWidth: "40%",
    // maxWidth: "100vw",
  },
});

const validationSchema = Yup.object().shape({
  // examID: Yup.string().required("Choissir l'examen"),
  // sessionID: Yup.string().required("Choissir la session"),
  // // centerName: Yup.string().required("Libellé Centre Obligatoire"),
  // centerNumber: Yup.string().required("Numéro de centre Obligatoire"),
});

const NewRegistrationReceipt = ({ id }) => {
  const classes = useStyles();
  const router = useRouter();
  const client = useApolloClient();

  const [state, setState] = useForm({});

  const loadRegistrationData = async () => {
    const { error, data } = await client.query({
      query: getCandidateRegistrationInfoQuery,
      variables: { id },
    });
    
    console.log(data);
    const { registration } = data;
    setState({ registration });
    return data;
  };

  useEffect(() => {
    loadRegistrationData();
  }, [id]);

  console.log(state.registration);
  const {
    aptitude,
    EPF1,
    EPF2,
    candidate,
    centerExamSessionSpecialty,
    createdAt,
    candRegistrationNumber,
  } = { ...state.registration };

  const { centerExamSession, specialty } = { ...centerExamSessionSpecialty };

  const { examSession, center } = { ...centerExamSession };
  const { exam, session } = { ...examSession };
  const { centerName } = { ...center };
  const { examName } = { ...exam };
  const { sessionName } = { ...session };
  const { specialtyName, subjectSpecialty } = { ...specialty };

  const {
    image,
    cand1stName,
    cand2ndName,
    cand3rdName,
    email,
    gender,
    placeOfBirth,
    dateOfBirth,
  } = { ...candidate };

  const initialValues = {
    image: "",
    cand1stName: "",
    cand2ndName: "",
    cand3rdName: "",
    email: "",
    gender: "",
    EPF1: "",
    EPF2: "",
    placeOfBirth: "",
    dateOfBirth: "",
  };

  return (
    <Formik
      method="POST"
      initialValues={initialValues}
      validationSchema={validationSchema}
    >
      {({ submitForm, setFieldValue, isSubmitting }) => {
        return (
          <div className={classes.centerAll}>
            <Paper className={classes.pageStyled} elevation={13}>
              <Form aria-busy={isSubmitting}>
                {isSubmitting && <LinearProgress />}
                <Grid className={classes.centerAll} container>
                  <Grid container className={classes.centerAll}>
                    <Grid item>
                      <Typography
                        className={classes.titleStyled}
                        color="primary"
                        gutterBottom
                        variant="h5"
                        component="h6"
                      >
                        Recu d'Inscription aux examens
                      </Typography>
                    </Grid>
                  </Grid>
                  <Grid
                    spacing={10}
                    container
                    className={classes.generalContainer}
                  >
                    <Grid
                      xs={12}
                      sm={6}
                      container
                      className={classes.centerAll}
                    >
                    <Grid item xs={12}>
                    <p> Center Info</p>
                    </Grid>
                    <Grid item xs={12}>
                    <p> registration  Info</p>
                    
                    </Grid>
                    
                    
                    
                    </Grid>
                    <Grid xs={12} sm={6} item className={classes.centerAll}>
                      {
                        <img
                          className={classes.picStyled}
                          src={image}
                          alt={cand1stName}
                        />
                      }
                    </Grid>
                  </Grid>
                  <Grid
                    spacing={10}
                    container
                    className={classes.generalContainer}
                  >
                    <Grid xs={12} sm={6} item className={classes.centerAll}>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Nom:</strong> <span>{cand1stName}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Prenoms: </strong>
                          <span> {cand2ndName}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Autres Noms:</strong>
                          <span> {cand3rdName}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <strong>Naissance:</strong>
                        <hr />
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Sexe:</strong> <span>{gender}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Date : </strong>
                          <span>{format(dateOfBirth, " DD, MMM, YYYY ")}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Lieu:</strong> <span>{placeOfBirth}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <strong>Nom des parents:</strong>
                        <hr />
                      </Typography>
                     
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Tel: </strong> <span>{phoneNumb}</span>
                        </p>
                      </Typography>
                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Email:</strong>
                          <span> {email}</span>
                        </p>
                      </Typography>

                      <Typography color="primary" variant="body1">
                        <p>
                          <strong>Code:</strong> <span> {candCode}</span>
                        </p>
                      </Typography>
                    </Grid>
                  </Grid>
                </Grid>
              </Form>
            </Paper>
          </div>
        );
      }}
    </Formik>
  );
};
export default NewRegistrationReceipt;
